import React from 'react';
import {Box, Button, Center, Flex, HStack, Image, Input, SimpleGrid, Text, VStack} from "@chakra-ui/react";
import {AddIcon, ArrowBackIcon} from "@chakra-ui/icons";

const QuizCreatePage = () => {
    return (
        <Box bg={'#F7FAFC'}>
            {/*  Header with action bar*/}
            <Box position={'fixed'} top={0} w={'100%'} bg={'#F7FAFC'} h={'200px'} maxH={'200px'}>
                <Box maxH={'60px'} bg={'teal.600'}>
                    <Flex align={'center'} p={2} alignItems={'center'} justifyContent={'space-between'}>
                        <ArrowBackIcon fontSize={35}/>
                        <Text>Editted 1 minute ago</Text>
                        <Button>Publish</Button>
                    </Flex>
                </Box>
                <Box h={'300px'} bg={'#F7FAFC'} maxW={'860px'}>
                    <Box>
                        <Center>
                            <HStack boxShadow={'md'} borderBottomRadius={'xl'} p={8} spacing={4} bg={'white'}>
                                <VStack w={'400px'}>
                                    <Text alignSelf={'start'}>Search</Text>
                                    <Input isFullWidth={true}/>
                                </VStack>
                                <VStack align={'end'} w={'200px'}>
                                    <Text>Create question</Text>
                                    <Button size={'md'} rightIcon={<AddIcon/>} variant={'solid'} colorScheme={'teal'}>Add
                                        question</Button>
                                </VStack>
                            </HStack>
                        </Center>
                    </Box>
                </Box>

            </Box>
            {/*   Content with right sidebar*/}
            <Box mt={'200[x'} minH={'100vh'} bg={'#F7FAFC'}>
                <Flex direction={'row'}>
                    {/*Grid*/}
                    <Box mt={10} w={'100%'} maxW={'860px'}>
                        <Center>
                            <SimpleGrid
                                boxShadow={'md'}
                                h={'100%'}
                                p={10}
                                bg={'white'}
                                align={'center'}
                                justifyContent={'center'}
                                justifyItems={'center'}
                                minChildWidth="100px"
                                maxW={'500px'}
                                spacing={2}>
                                {
                                    [1, 2, 2, 2, 2, 2].map((item, index) => (
                                        <VStack cursor={'pointer'} key={index}>
                                            <Image
                                                boxSize="80px"
                                                objectFit="cover"
                                                src="https://cdn-icons-png.flaticon.com/512/1168/1168671.png"
                                                alt="Segun Adebayo"
                                            />
                                            <Text
                                                fontSize={15}
                                                color={'gray.600'}
                                                fontWeight={'medium'}
                                            >Checkbox</Text>
                                        </VStack>
                                    ))
                                }
                            </SimpleGrid>
                        </Center>
                    </Box>

                </Flex>

            </Box>
        </Box>
    );
};

export default QuizCreatePage;
