import React, {useEffect, useState} from 'react';
import {
    Box,
    Button,
    Center,
    Flex,
    Grid,
    GridItem,
    SimpleGrid,
    Input,
    Stack,
    Wrap,
    WrapItem,
    Heading, Text, HStack, Avatar, VStack, Image
} from "@chakra-ui/react";
import Card from "./Card";
import Footer from "../Footer";

import {GoFlame} from "react-icons/all";
import {useAuth} from "../../hooks/useAuth";

const HomeScreen = () => {
    const {user, logout} = useAuth();

    const [found, setFound] = useState(true);

    useEffect(() => {

    }, []);

    return (
        <Flex direction={'column'} bg={'#F7FAFC'} alignItems={'center'} justifyContent={'center'}>
            {/*//Main Content*/}
            <Flex
                pt={8}
                bg={'#F7FAFC'}
                // minHeight={'100vh'}
                w={'100%'}
                direction={'column'}
                alignItems={'flex-start'}
            >
                <Flex w={'100%'} justifyContent={'space-around'} alignItems={'center'}>
                    {/*Join Code*/}
                    <Box
                        // minW={'800px'}
                        w={['100px', '500px', '800px']}
                    >
                        <Stack boxShadow={'md'}
                               borderRadius={'10px'}
                               bg={'white'}
                               p={['10px', '50px', '100px']}
                               minW={'360px'}
                               direction={'row'}

                        >
                            <Input minH={'50px'} minW={'260px'} placeholder={'Enter a join code'}/>
                            <Button
                                onClick={() => {
                                    setFound(false);
                                }}
                                minH={'50px'} minW={'30px'}
                                bgGradient="linear(to-l, #7928CA, #FF0080)"
                                color={'white'}>Join</Button>
                        </Stack>
                    </Box>
                    {/*Day Streak*/}


                    {
                        !found && (
                            <Box
                                boxShadow={'2xl'}
                                transform={
                                    'translate(50%,-50%)'
                                }
                                maxW={'350px'}
                                borderRadius={'xl'}
                                position={'fixed'}
                                bg={'pink.700'}
                                top={'50%'}
                                right={'50%'}
                                align={'center'}
                                zIndex={1000}>
                                <VStack p={5} align={'start'}>
                                    <Heading size={'20px'}>
                                        Cound not find quiz with ID : quizID
                                    </Heading>
                                    <Text>Please try another code</Text>
                                    <Button
                                        bg={'pink.700'}
                                        color={'white'}
                                        alignSelf={'end'}
                                            onClick={
                                                () => {
                                                    setFound(true)
                                                }
                                            }>Got it
                                    </Button>
                                </VStack>
                            </Box>
                        )
                    }
                    <Box
                        boxShadow={'md'}
                        borderRadius={'10px'}
                        bg={'white'}
                        p={['10px', '50px']}
                        align={'center'}>
                        <Stack direction={'column'}>
                            <Box bg={'#EDF2F7'} p={1} borderRadius={'200px'} minW={'200px'}>
                                <HStack>
                                    {/*avt*/}
                                    <Avatar
                                        name={user != null ? user.username : ''}
                                        src={
                                            (user != null && user.avtImgUrl != null) ? user.avtImgUrl : "https://cdn-icons-png.flaticon.com/512/2021/2021646.png"
                                        }
                                    />
                                    {/*username*/}
                                    <Text
                                        fontSize={'16px'} color={'#4A5568'}
                                        fontWeight={'medium'}>
                                        {user != null ? user.username : 'Create an account'}
                                    </Text>
                                </HStack>
                            </Box>
                            <HStack spacing={2} pt={1} pb={1}>
                                <GoFlame color={'red'} fontSize={'30px'}/>
                                <GoFlame color={'red'} fontSize={'30px'}/>
                                <GoFlame color={'#718096'} fontSize={'30px'}/>
                                <GoFlame color={'#718096'} fontSize={'30px'}/>
                                <GoFlame color={'#718096'} fontSize={'30px'}/>
                            </HStack>
                            <Text color={'#4A5568'} fontWeight={'bold'} fontSize={'16px'}>0 day streak</Text>
                            <Text color={'#718096'} fontSize={'14px'}>Start your streak, play now!</Text>
                        </Stack>
                    </Box>
                </Flex>
            </Flex>
            {/*Recent Acitvity*/}
            <Box py={5} px={10} align={'center'}>
                <VStack align={'start'}>
                    <Text fontSize={'22px'} letterSpacing={1} fontWeight={'medium'}>Recent Activity</Text>
                    <Flex pt={5} justifyContent={'space-around'} w={'100%'}>
                        <Card mr={2}/>
                        <Card mr={2}/>
                        <Card mr={2}/>
                        <Card mr={2}/>
                        <Card mr={2}/>
                    </Flex>
                </VStack>
            </Box>
            {/*End of RecentActivity*/}
            {/*Recent Acitvity*/}
            <Box px={10} py={5} align={'center'}>
                <VStack align={'start'}>
                    <Text fontSize={'22px'} letterSpacing={1} fontWeight={'medium'}>Recent Activity</Text>
                    <Flex pt={5} justifyContent={'space-around'} w={'100%'}>
                        <Card mr={2}/>
                        <Card mr={2}/>
                        <Card mr={2}/>
                        <Card mr={2}/>
                        <Card mr={2}/>
                    </Flex>
                </VStack>
            </Box>
            {/*End of RecentActivity*/}
            {/**/}
            <Box minH={'100px'}></Box>
            {/*Footer*/}

            <Footer/>

            {/*    end of main content*/}
        </Flex>
    );
};

export default HomeScreen;
