import React, {createContext, useEffect, useState} from "react";
import Axios from "axios";
import axios from "axios";

export const AuthContext = createContext();

export const AuthProvider = ({children}) => {

    const [user, setUser] = useState(null);
    const [isAuthenticated, setAuthenticated] = useState(false);

    const [isLoading, setLoading] = useState(true);
    //login
    const login = async (email, password, remember) => {
        let url = "http://localhost:8080/api/v1/auth/login";
        let data = {
            "email": email,
            "password": password
        };

        try {
            const resp = await axios.post(url, data, {
                headers: {"Content-Type": "application/json"}
            })
            if (resp.status === 200) {
                localStorage.setItem("accessToken", resp.data.access_token);
                localStorage.setItem("refreshToken", resp.data.refresh_token);
                return true;
            }
        } catch (e) {
            return false;
        }

    };

    //logout
    const logout = () => {
        setUser(null);
        localStorage.removeItem('user')
        localStorage.removeItem('accessToken')
        localStorage.removeItem('refreshToken')
    }
    const getUserDetails = () => {
        if (localStorage.getItem('accessToken') != null) {
            setLoading(true);
            let url = "http://localhost:8080/api/v1/member/users/user-details";
            axios.get(url, {
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${localStorage.getItem('accessToken')}`
                }
            })
                .then(resp => {
                    if (resp.status === 200) {
                        setUser(resp.data);
                        console.log(resp.data);
                        localStorage.setItem("user", JSON.stringify(resp.data));
                        return;
                    } else if (resp.status === 401) {
                        //refresh token
                    } else {
                        logout();
                    }
                }).catch(
                () => {
                    logout();
                    setLoading(false);
                }
            ).finally(() => {
                setLoading(false);
            })
        }
    }

    //create value
    const value = {
        user,
        setUser,
        login,
        logout,
        isAuthenticated,
        setAuthenticated,
        getUserDetails,
    };

    useEffect(() => {
        setLoading(true);
        const accessToken = localStorage.getItem("accessToken");
        if (accessToken != null) {
            setLoading(true);
            let url = "http://localhost:8080/api/v1/member/users/user-details";
            axios.get(url, {
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${accessToken}`
                }
            })
                .then(resp => {
                    if (resp.status === 200) {
                        setUser(resp.data);
                        console.log(resp.data);
                        localStorage.setItem("user", JSON.stringify(resp.data));
                        return;
                    } else if (resp.status === 401) {
                        //refresh token
                    } else {
                        logout();
                    }
                }).catch(
                () => {
                    logout();
                    setLoading(false);
                }
            ).finally(() => {
                setLoading(false);
            })

        } else {
            setLoading(false)
        }
    }, []);
    return (
        <>
            {
                !isLoading && <AuthContext.Provider value={value}>{children}</AuthContext.Provider>
            }
        </>
    )
        ;
};
