import React from 'react';
import {Box, Button, Center, Flex, Grid, GridItem, SimpleGrid, Input, Stack, Wrap, WrapItem} from "@chakra-ui/react";
import Card from "./card";
import Footer from "./footer";

const Home = () => {
    return (
        <Flex  direction={'column'}>
            {/*//Main Content*/}
            <Flex
                p={8}
                bg={'#F7FAFC'}
                minHeight={'100vh'}
                w={'100%'}
                direction={'column'}
                alignItems={'flex-start'}
            >
                <Box
                    // minW={'800px'}
                    w={['100px','500px','800px']}
                >
                    <Stack boxShadow={'md'}
                           borderRadius={'10px'}
                           bg={'white'}
                           p={['20px','50px','100px']}
                           minW={'360px'}
                           direction={'row'}

                    >
                        <Input minH={'50px'} minW={'300px'} placeholder={'Enter a join code'}/>
                        <Button minH={'50px'} minW={'50px'}
                                bgGradient="linear(to-l, #7928CA, #FF0080)"
                                color={'white'}>Join</Button>
                    </Stack>
                </Box>

                <Box minHeight={'100vh'}>
                    sd
                </Box>
                <Footer/>

            </Flex>
            {/*    end of main content*/}
        </Flex>
    );
};

export default Home;
