import React, {useContext, useEffect, useRef} from 'react';
import {
    Button,
    FormControl, FormLabel, Input,
    Modal,
    ModalBody,
    ModalCloseButton,
    ModalContent, ModalFooter,
    ModalHeader,
    ModalOverlay, Select
} from "@chakra-ui/react";
import {QuizEditContext} from "../../../../providers/QuizEditProvider";

const QuizEditDialog = ({isOpen, setOpen}) => {

    // const [quiz, setQuiz] = useContext(QuizEditContext);
    const {quiz, setQuiz, isLoading, setLoading} = useContext(QuizEditContext);

    const initialRef = useRef();
    const finalRef = useRef();
    useEffect(() => {
console.log('eff')
    }, [])

    return (
        <>
            <Modal
                initialFocusRef={initialRef}
                finalFocusRef={finalRef}
                isOpen={isOpen}
                onClose={() => setOpen(false)}
            >
                <ModalOverlay/>
                <ModalContent>
                    <ModalHeader>Edit Quiz</ModalHeader>
                    <ModalCloseButton/>
                    <ModalBody pb={6}>
                        <FormControl>
                            <FormLabel>Quiz title</FormLabel>
                            <Input
                                value={quiz && quiz.title}
                                ref={initialRef} placeholder="Quiz title"/>
                        </FormControl>

                        <FormControl mt={4}>
                            <FormLabel>Subject</FormLabel>
                            <Select placeholder="Select option" size={'sm'}>
                                <option value="option1">Option 1</option>
                                <option value="option2">Option 2</option>
                                <option value="option3">Option 3</option>
                            </Select>
                        </FormControl>
                        <FormControl mt={4}>
                            <FormLabel>Subject</FormLabel>
                            <Select placeholder="Select option" size={'sm'}>
                                <option value="option1">Option 1</option>
                                <option value="option2">Option 2</option>
                                <option value="option3">Option 3</option>
                            </Select>
                        </FormControl>
                    </ModalBody>

                    <ModalFooter>
                        <Button colorScheme="blue" mr={3}>
                            Save
                        </Button>
                        <Button onClick={() => setOpen(false)}>Cancel</Button>
                    </ModalFooter>
                </ModalContent>
            </Modal>

        </>
    );
};

export default QuizEditDialog;
