import React, {useState} from 'react';
import {
    Box,
    Button,
    Center, Checkbox, Container,
    Flex, Heading,
    HStack,
    IconButton,
    Image,
    Input, Select,
    SimpleGrid,
    Spacer, Stack, Tag, TagLabel,
    Text, Tooltip,
    VStack
} from "@chakra-ui/react";
import {AddIcon, ArrowBackIcon, CopyIcon, DeleteIcon, EditIcon, TimeIcon, ViewIcon} from "@chakra-ui/icons";
import {AiOutlineTag, BiPen, BsPen} from "react-icons/all";
import EditQuestionDialog from "./EditQuestionDialog";
import {useQuiz} from "../../context/QuizProvider";
import QuestionEntry from "./QuestionEntry";

const EditQuizPage = () => {
    const [showCreateQ, setShowCQD] = useState(false);

    const {savedQuestions, setSavedQuestions} = useQuiz();
    return (
        <Box bg={'#F7FAFC'}>
            {/*  Header with action bar*/}
            {
                showCreateQ && <EditQuestionDialog setVisible={setShowCQD}/>
            }

            <HeaderWithActionBar setShowCQD={setShowCQD}/>
            {/*   Content with right sidebar*/}
            <Box pt={'150px'} minH={'100vh'} bg={'#F7FAFC'} position={'relative'}>
                <Flex direction={'row'}>
                    {/*Question*/}
                    <Box flex={7} mt={10} w={'100%'} maxW={'860px'}>
                        <Center>
                            {/*SAVED QUESTIONS*/}
                            <Stack w={'100%'} direction={'column'} align={'center'}>
                                {
                                    savedQuestions.length > 0 && savedQuestions.map((item, i) => (
                                        <QuestionEntry key={i} index={i + 1} question={item}/>
                                        // <Box boxShadow={'sm'} key={i} my={5} borderRadius={'md'} align={'start'}
                                        //      textAlign={'start'}
                                        //      h={'100%'} bg={'white'} maxW={'600px'}
                                        //      minW={'600px'}>
                                        //     {/*Edit bar*/}
                                        //     <HStack p={2} bg={'gray.200'}>
                                        //
                                        //         <Text>Question1</Text>
                                        //         <Spacer/>
                                        //         <Tooltip label={'edit'} color={'teal.200'}>
                                        //             <IconButton icon={<BsPen/>}/>
                                        //         </Tooltip>
                                        //         <Tooltip color={'red.500'} label={'remove'}>
                                        //             <IconButton icon={<DeleteIcon/>}/>
                                        //         </Tooltip>
                                        //         <Tooltip label={'duplicate'}>
                                        //             <IconButton icon={<CopyIcon/>}/>
                                        //         </Tooltip>
                                        //     </HStack>
                                        //     <VStack align={'start'} p={2} spacing={5}>
                                        //         {/*    Question title*/}
                                        //         <Container maxW="3xl">
                                        //             <Box maxW="3xl">
                                        //                 <Text fontWeight={'medium'}>This is a question</Text>
                                        //             </Box>
                                        //         </Container>
                                        //         <SimpleGrid columns={2} spacing={2}>
                                        //             {
                                        //                 [1, 2, 3, 4].map((item, index) => (
                                        //                     <Container key={index} maxW="xl" centerContent>
                                        //                         <Box padding="1" maxW="3xl">
                                        //                             <Checkbox size={'sm'}>
                                        //                                 There are many benefits to a joint design and
                                        //                                 development system. Not only
                                        //                                 does it bring benefits to the design team.
                                        //                             </Checkbox>
                                        //                         </Box>
                                        //                     </Container>
                                        //                 ))
                                        //             }
                                        //         </SimpleGrid>
                                        //     </VStack>
                                        //     <HStack bg={'gray.200'} p={1}>
                                        //         <Select size={'sm'} maxW={'100'} bg={'white'} name={'queston seconds'}>
                                        //             <option value="option1">30 secs</option>
                                        //             <option value="option2">15 secs</option>
                                        //             <option value="option3">45 secs</option>
                                        //         </Select>
                                        //         <Button size={'sm'} colorScheme={'white'} variant={'ghost'}
                                        //                 leftIcon={<AiOutlineTag/>}>Tag topics</Button>
                                        //     </HStack>
                                        // </Box>
                                    ))
                                }
                            </Stack>
                        </Center>
                    </Box>
                    {/**/}
                    <Box
                        overflowY={'scroll'}
                        top={'150'}
                        zIndex={10}
                        position={'sticky'}
                        bg={'white'}
                        boxShadow={'md'}
                        minW={'300px'}
                        maxW={'300px'}
                        minH={'400px'}
                        height={'100%'}
                    >
                        <Box p={4} mb={100}>
                            <VStack spacing={5}>
                                <Input type={'file'}/>
                                <HStack>
                                    <Tooltip label={'Quiz title'}>
                                        <Heading fontWeight={'medium'} fontSize={'25px'}>This is quiz title</Heading>
                                    </Tooltip>
                                    <IconButton
                                        colorScheme={'teal'}
                                        onClick={() => {
                                        }}
                                        icon={<EditIcon/>}/>
                                </HStack>
                                <HStack>
                                    <Tooltip
                                        zIndex={100}
                                        label={'Public: visible to everyone ;\n Private: visible to you and shared ones'}>
                                        <Button size={'sm'} leftIcon={<ViewIcon/>} variant={'solid'}
                                                colorScheme={'white'} color={'blue.400'}>public</Button>
                                    </Tooltip>
                                    <Tooltip zIndex={100} label={'Default time for all questions'}>
                                        <Button size={'sm'} leftIcon={<TimeIcon/>} variant={'solid'}
                                                colorScheme={'white'} color={'blue.400'}>30 secs</Button>
                                    </Tooltip>
                                </HStack>

                            </VStack>
                        </Box>
                    </Box>
                </Flex>

            </Box>
        </Box>
    );
};

export default EditQuizPage;


const HeaderWithActionBar = ({setShowCQD}) => {
    return (
        <Box zIndex={1000} bg={'red'} position={'fixed'} top={0} w={'100%'} bg={'#F7FAFC'}>
            {/*Header*/}
            <Box bg={'red'} maxH={'60px'} bg={'teal.600'}>
                <Flex align={'center'} px={8} py={2} alignItems={'center'} justifyContent={'space-between'}>
                    <ArrowBackIcon size={'md'} color={'white'}/>
                    <Text color={'white'} fontWeight={'medium'} fontStyle={'italic'}>Editted 1 minute ago</Text>
                    <Button size={'sm'}>Publish</Button>
                </Flex>
            </Box>
            {/*Box*/}
            <Box bg={'#F7FAFC'} maxW={'860px'}>
                <Box>
                    <Center>
                        <HStack boxShadow={'md'} p={4} borderBottomRadius={'xl'} spacing={4} bg={'white'}>
                            <VStack w={'400px'}>
                                <Text alignSelf={'start'}>Search</Text>
                                <Input/>
                            </VStack>
                            <VStack align={'end'} w={'200px'}>
                                <Text>New question</Text>
                                <Button
                                    onClick={() => {
                                        setShowCQD(true);
                                    }}
                                    size={'md'}
                                    rightIcon={<AddIcon/>}
                                    variant={'solid'}
                                    colorScheme={'teal'}>
                                    New
                                    question</Button>
                            </VStack>
                        </HStack>
                    </Center>
                </Box>
            </Box>
        </Box>
    );
}
