import React from 'react';
import {Box, Button, Center, Checkbox, Container, HStack, Input, Text, Textarea, VStack} from "@chakra-ui/react";

const QuestionEditDialog = ({setVisible}) => {


    return (
        <>
            <Box
                onClick={() => {
                    console.log("HAHHAHA")
                }}
                position={'fixed'}
                zIndex={9000}
                top={0}
                bg={'gray.300'}
                align={'center'}
                alignItems={'center'}
                opacity={.4}
                justifyContent={'center'}
                width={'100vw'}
                height={'100vh'}>
            </Box>

            <Box boxShadow={'md'}
                 position={'fixed'}
                 zIndex={9001}
                 top={'50%'}
                 right={'50%'}
                 transform={
                     'translate(50%,-50%)'
                 }

                 minW={'500px'}
                 bg={'white'}
                 maxW={'500px'}
            >
                <VStack>
                    <HStack bg={'gray.700'} p={2} w={'100%'}>
                        <Text color={'white'} fontWeight={'medium'}>Question 2</Text>
                    </HStack>
                    {/*Question input title*/}
                    <Box p={2} w={'100%'}>
                        <Textarea
                            // css={
                            //     {
                            //         '&::-webkit-scrollbar': {
                            //             width: '0px',
                            //             background: 'transparent'
                            //         }
                            //     }
                            // }
                            minH={'50px'}
                            maxH={'100px'}
                            onInput={auto_grow(this)}
                            resize={'none'}
                            overflow={'hidden'}
                            placeholder={'Type your question here'}/>
                    </Box>
                    <VStack p={4} spacing={4} w={'100%'}>
                        {
                            [1, 2, 3, 4].map((item, index) => (
                                <HStack w={'100%'}>
                                    <Checkbox/>
                                    <Input wordBreak={'break-all'} wordWrap={'break-word'}
                                           isFullWidth={true}
                                           size={'sm'}/>
                                </HStack>
                            ))
                        }
                    </VStack>
                    <Box align={'start'} p={2} w={'100%'}> <Button size={'sm'} variant={'outline'} alignSelf={'start'}
                                                                   colorScheme={'teal'}>
                        Add answer option</Button></Box>
                    <Box w={'100%'}>
                        <HStack p={2} bg={'gray.700'} justifyContent={'end'}>
                            <Button onClick={() => {
                                setVisible(false)
                            }} size={'sm'}>Cancel</Button>
                            <Button onClick={() => {
                                setVisible(false)
                            }} size={'sm'}>Save</Button>
                        </HStack>
                    </Box>
                </VStack>
            </Box>
        </>

    );
};

export default QuestionEditDialog;
