import React, {useState} from 'react';
import {
    Box,
    Button,
    Center, Checkbox,
    Flex,
    Grid,
    GridItem,
    Heading,
    HStack, IconButton,
    Image, Input,
    SimpleGrid, Spacer,
    Text, Tooltip,
    VStack
} from "@chakra-ui/react";
import {AddIcon, ArrowBackIcon, CopyIcon, DeleteIcon, EditIcon, TimeIcon, ViewIcon} from "@chakra-ui/icons";
import CreateQuizDialog from "../home/CreateQuizDialog";

const CreateQuestionPage = () => {

    const [showCreateQuiz, setShowCreateQuiz] = useState(false);
    const [showQuestionTypeDialog, setShowQT] = useState(false);

    return (
        <Box
            align={'center'}
            bg={'#F7FAFC'}
            minH={'100vh'}>

            {/*Show edit quiz dialog*/}
            {
                showCreateQuiz &&
                <CreateQuizDialog
                    setShow={setShowCreateQuiz}
                    onClose={() => {
                        setShowCreateQuiz(false);
                    }}/>
            }


            {/*Header*/}
            <Box minH={'50px'} maxH={'80px'} bg={'teal.600'} w={'100%'}>
                <Flex alignItems={'center'} justifyContent={'space-between'} p={4}>
                    <ArrowBackIcon fontSize={35}/>
                    <Text>Editted 1 minute ago</Text>
                    <Button>Publish</Button>
                </Flex>
            </Box>
            {/*    Content*/}
            <Flex alignItems={'center'} justifyContent={'center'}>
                {/**/}
                <Box minH={'70vh'} flex={'7'} justifyItems={'start'} justifyContent={'start'}>
                    <Flex alignSelf={'start'} direction={'column'} justifyContent={'center'} alignItems={'center'}>
                        {/*Create question dialog*/}
                        <Box
                            bg={'white'}
                            // maxW={'600px'}
                            boxShadow={'md'}
                            borderBottomRadius={'3xl'}
                            // p={10}
                            p={8}
                        >
                            <HStack spacing={4}>
                                <VStack w={'400px'}

                                >
                                    <Text alignSelf={'start'}>Search</Text>
                                    <Input isFullWidth={true}/>
                                </VStack>
                                <VStack align={'end'} w={'200px'}>
                                    <Text>Create question</Text>
                                    <Button size={'md'} rightIcon={<AddIcon/>} variant={'solid'} colorScheme={'teal'}>Add
                                        question</Button>
                                </VStack>
                            </HStack>
                        </Box>
                        <Box minH={'20px'}/>
                        {/*Question type dialog*/}
                        {showQuestionTypeDialog &&
                        <SimpleGrid
                            h={'100%'}
                            p={'10'}
                            bg={'white'}
                            align={'center'}
                            justifyContent={'center'}
                            justifyItems={'center'}
                            minChildWidth="100px"
                            maxW={'500px'}
                            spacing={2}>
                            {
                                [1, 2, 2, 2, 2, 2].map((item, index) => (
                                    <VStack cursor={'pointer'} key={index}>
                                        <Image
                                            boxSize="80px"
                                            objectFit="cover"
                                            src="https://cdn-icons-png.flaticon.com/512/1168/1168671.png"
                                            alt="Segun Adebayo"
                                        />
                                        <Text
                                            fontSize={15}
                                            color={'gray.600'}
                                            fontWeight={'medium'}
                                        >Checkbox</Text>
                                    </VStack>
                                ))
                            }
                        </SimpleGrid>
                        }
                        {/*    Created answers*/}
                        <Box  align={'start'} textAlign={'start'} h={'100%'} bg={'teal'} maxW={'600px'}
                             minW={'600px'}>
                            {/*Edit bar*/}
                            <HStack bg={'gray.200'} p={2}>
                                <Text>Question1</Text>
                                <Spacer/>
                                <Button>Edit</Button>
                                <IconButton icon={<DeleteIcon/>}/>
                                <IconButton icon={<CopyIcon/>}/>
                            </HStack>
                            {/*    Question title*/}
                            <Text>This is question 1</Text>
                            <SimpleGrid p={2} columns={2} spacing={10}>
                                <Checkbox size={'md'}>
                                    This is question1asdfffffffffffffffffffffffffffffffffff
                                </Checkbox> <Checkbox size={'md'}>
                                This is question1asdfffffffffffffffffffffffffffffffffff
                            </Checkbox> <Checkbox size={'md'}>
                                This is question1asdfffffffffffffffffffffffffffffffffff
                            </Checkbox> <Checkbox size={'md'}>
                                This is question1asdfffffffffffffffffffffffffffffffffff
                            </Checkbox>


                            </SimpleGrid>
                        </Box>

                    </Flex>
                </Box>
                {/*Right Side Bar*/}
                <Box minH={'70vh'} flex={'3'} justifyItems={'start'} justifyContent={'start'}>
                    <Box bg={'white'} p={5} borderRadius={'md'} maxW={'300px'} w={'100%'} boxShadow={'md'}>
                        <VStack spacing={5}>
                            <Input type={'file'}/>
                            <HStack>
                                <Tooltip label={'Quiz title'}>
                                    <Heading fontWeight={'medium'} fontSize={'25px'}>This is quiz title</Heading>
                                </Tooltip>
                                <IconButton
                                    colorScheme={'teal'}
                                    onClick={() => {
                                        setShowCreateQuiz(true)
                                    }}
                                    icon={<EditIcon/>}/>
                            </HStack>
                            <HStack>
                                <Tooltip
                                    label={'Public: visible to everyone ;\n Private: visible to you and shared ones'}>
                                    <Button size={'sm'} leftIcon={<ViewIcon/>} variant={'solid'}
                                            colorScheme={'white'} color={'blue.400'}>public</Button>
                                </Tooltip>
                                <Tooltip label={'Default time for all questions'}>
                                    <Button size={'sm'} leftIcon={<TimeIcon/>} variant={'solid'}
                                            colorScheme={'white'} color={'blue.400'}>30 secs</Button>
                                </Tooltip>
                            </HStack>

                        </VStack>
                    </Box>
                </Box>

            </Flex>


        </Box>
    );
};

export default CreateQuestionPage;
