import React from 'react';

import {
    Box,
    Center,
    useColorModeValue,
    Heading,
    Text,
    Stack,
    Image,
} from '@chakra-ui/react';

// const IMAGE =
//     'https://images.unsplash.com/photo-1518051870910-a46e30d9db16?ixlib=rb-1.2.1&ixid=MXwxMjA3fDB8MHxwaG90by1
const IMAGE =
 'https://cdn-icons-png.flaticon.com/512/4193/4193243.png';

 const Card = ({...rest}) => {
     return (
         <Center {...rest} >
             <Box
                 boxShadow={'md'}
                 role={'group'}
                 p={2}
                 maxW={'250px'}
                 w={'full'}
                 bg={useColorModeValue('white', 'gray.800')}
                 // boxShadow={'2xl'}
                 rounded={'lg'}
                 pos={'relative'}
                 zIndex={1}>
                 {/*Image*/}
                 <Box
                     rounded={'lg'}
                     // mt={-12}
                     pos={'relative'}
                     // height={'230px'}
                     // _after={{
                     //     transition: 'all .3s ease',
                     //     content: '""',
                     //     w: 'full',
                     //     h: 'full',
                     //     pos: 'absolute',
                     //     top: 5,
                     //     left: 0,
                     //     backgroundImage: `url(${IMAGE})`,
                     //     filter: 'blur(15px)',
                     //     zIndex: -1,
                     // }}
                     _groupHover={{
                         _after: {
                             filter: 'blur(20px)',
                         },
                     }}
                 >
                     <Image
                         p={4}
                         rounded={'lg'}
                         height={200}
                         width={200}
                         objectFit={'cover'}
                         src={IMAGE}
                     />
                 </Box>
                 {/*End of Image*/}
                 
                 <Stack  align={'center'}>
                     <Text color={'gray.500'} fontSize={'sm'} textTransform={'uppercase'}>
                         Brand
                     </Text>
                     <Heading fontSize={'2xl'} fontFamily={'body'} fontWeight={500}>
                         Nice Chair, pink
                     </Heading>
                     <Stack direction={'row'} align={'center'}>
                         <Text fontWeight={800} fontSize={'xl'}>
                             $57
                         </Text>
                         <Text textDecoration={'line-through'} color={'gray.600'}>
                             $199
                         </Text>
                     </Stack>
                 </Stack>
             </Box>
         </Center>
     );
};
 export  default Card;
