import React, {useState} from 'react';
import {
    Box,
    Button,
    Center,
    Checkbox,
    Container,
    HStack,
    Input,
    Select,
    Text,
    Textarea,
    VStack
} from "@chakra-ui/react";
import {MdArrowDropDown} from "react-icons/all";

const EditQuestionDialog = ({setVisible}) => {


    const [numOfQuestions, setNumOfQuestion] = useState([0, 1, 2, 3]);


    const [questions, setQuestions] = useState({
        title: 'this is question title',
        // options: ['', '', '', '']
        options: [{content: 'this is option 1'}, {content: ''}, {content: ''}, {content: ''}]
    })

    //on title change
    const onQuizTitleChange = (e) => {
        setQuestions(prevState => ({
            ...prevState,
            title: e.target.value
        }))
    }

    const onOptionChange = (e, i) => {

        setQuestions(prevState => ({
            ...prevState,
            options: prevState.options.map((savedOption, index) => {
                if (i === index) {
                    return {...prevState.options[i], content: e.target.value};
                }
                return savedOption;
            })
        }))
    }

    const onAddOption = () => {
        setQuestions(prevState => ({
            ...prevState,
            title: prevState.title,
            options: [...prevState.options, {content: ''}]
        }))
    }

    const onCheckBoxChange = (e, optionID) => {
        setQuestions(prevState => ({
            ...prevState,
            options: prevState.options.map((savedOption, index) => {
                if (optionID === index) {
                    return {...prevState.options[optionID], isChecked: e.target.checked};
                }
                return savedOption;
            })
        }))
    }

    function onScoreChange(e, optionId) {
        setQuestions(prevState => ({
            ...prevState,
            options: prevState.options.map((savedOption, index) => {
                if (optionId === index) {
                    return {...prevState.options[optionId], score: e.target.value};
                }
                return savedOption;
            })
        }))
    }

    return (
        <>
            <Box
                onClick={() => {
                    console.log("HAHHAHA")
                }}
                position={'fixed'}
                zIndex={9000}
                top={0}
                bg={'gray.300'}
                align={'center'}
                alignItems={'center'}
                opacity={.4}
                justifyContent={'center'}
                width={'100vw'}
                height={'100vh'}>
            </Box>

            <Box boxShadow={'md'}
                 position={'fixed'}
                 zIndex={9001}
                 top={'50%'}
                 right={'50%'}
                 transform={
                     'translate(50%,-50%)'
                 }

                 minW={'700px'}
                 bg={'white'}
                 maxW={'700px'}
            >
                <VStack>
                    <HStack bg={'gray.700'} p={2} w={'100%'}>
                        <Text color={'white'} fontWeight={'medium'}>Question 2</Text>
                    </HStack>
                    {/*Question input title*/}
                    <Box p={2} w={'100%'}>
                        <Textarea
                            onChange={(e) => onQuizTitleChange(e)}
                            value={questions.title}
                            css={
                                {
                                    '&::-webkit-scrollbar': {
                                        width: '0px',
                                        background: 'transparent'
                                    }
                                }
                            }
                            resize={'none'}
                            overflow={'hidden'}
                            placeholder={'Type your question here'}/>
                    </Box>
                    <VStack p={4} spacing={4} w={'100%'}>
                        {
                            questions.options.map((item, i) => (
                                <HStack key={i} w={'100%'}>
                                    <Checkbox onChange={(e) => onCheckBoxChange(e, i)}/>
                                    <Input
                                        value={questions.options[i].content}
                                        onChange={(e) => onOptionChange(e, i)}
                                        wordBreak={'break-all'}
                                        wordwrap={'break-word'}
                                        // isFullWidth
                                        size={'md'}/>
                                    <Select
                                        onChange={(e) => onScoreChange(e, i)}
                                        isDisabled={item.isChecked == null || item.isChecked === false}
                                        defaultValue={0.25}
                                        icon={<MdArrowDropDown/>}
                                        iconSize={0}
                                        maxW={70}
                                        size="xs"
                                        variant="outline">
                                        {
                                            [0, 0.25, .5, .75, 1, 1.25, 1.5, 1.75, 2]
                                                .map((score, index) => (
                                                    <option key={index} value={score}>{score}</option>
                                                ))
                                        }
                                    </Select>
                                </HStack>
                            ))
                        }

                    </VStack>
                    {/*BUTTON ADD ANSWER*/}
                    <Box align={'start'} p={2} w={'100%'}>
                        <Button
                            onClick={onAddOption}
                            size={'sm'}
                            variant={'outline'}
                            alignSelf={'start'}
                            colorScheme={'teal'}>
                            Add answer option
                        </Button>
                    </Box>
                    {/*BUTTON SAVE*/}
                    <Box w={'100%'}>
                        <HStack p={2} bg={'gray.700'} justifyContent={'end'}>
                            <Button onClick={() => {
                                setVisible(false)
                            }} size={'sm'}>Cancel</Button>
                            <Button onClick={() => {
                                setVisible(false)
                            }} size={'sm'}>Save</Button>
                        </HStack>
                    </Box>
                </VStack>
            </Box>
        </>

    );
};

export default EditQuestionDialog;
