                    <VStack spacing={10}>
                        <VStack w={300}>
                            <Button
                                onClick={()=>{
                                    setShow(false)
                                }}
                                colorScheme={'teal'} size={'lg'} w={400}>Start</Button>
                            <Button size={'lg'} w={400}>Challenge</Button>
                            <Button leftIcon={<BiShare/>} size={'lg'} w={400}>Share</Button>
                        </VStack>
                        <Divider colorScheme={'teal'}/>
