                        {/*                            <FormLabel htmlFor="email-alerts" mb="0">*/}
                        {/*                                Music*/}
                        {/*                            </FormLabel>*/}
                        {/*                            <Switch id="email-alerts"/>*/}
                        {/*                        </FormControl>*/}
                        {/*                    </GridItem>*/}
                        {/*                ))*/}
                        {/*            }*/}
                        {/*        </SimpleGrid>*/}
                        {/*        <Divider/>*/}
                        {/*    </VStack>*/}
                        {/*</Box>*/}

                    </VStack>
                </Box>

                <Box flex={3} align={'center'}>
                </Box>

            </Flex>
        </>
    );
};

export default QuizOptionDialog;
