import React from 'react';
import {Box, Button, Center, Flex, Grid, GridItem, Heading, Image, SimpleGrid, Text, VStack} from "@chakra-ui/react";
import {ArrowBackIcon} from "@chakra-ui/icons";

const CreateQuestionPage = () => {
    return (
        <Box
            align={'center'}
            bg={'#F7FAFC'}
            minH={'100vh'}>
            {/*Header*/}
            <Box minH={'60px'} maxH={'80px'} bg={'teal.600'} w={'100%'}>
                <Flex alignItems={'center'} justifyContent={'space-between'} p={4}>
                    <ArrowBackIcon fontSize={35}/>
                    <Text>Editted 1 minute ago</Text>
                    <Button>Publish</Button>
                </Flex>
            </Box>
            {/*    Content*/}
            <Flex alignItems={'center'} justifyContent={'center'}>
                <Box flex={'7'}>
                   <Flex>
                       <Box minH={'300px'} bg={'red'}>
                           ffd
                       </Box>
                       <SimpleGrid
                           align={'center'}
                           justifyContent={'center'}
                           justifyItems={'center'}
                           minChildWidth="120px"
                           maxW={'500px'}
                           spacing={3}>
                           {
                               [1, 2, 2, 2, 2, 2].map((item, index) => (
                                   <VStack cursor={'pointer'} key={index}>
                                       <Image
                                           boxSize="100px"
                                           objectFit="cover"
                                           src="https://cdn-icons-png.flaticon.com/512/1168/1168671.png"
                                           alt="Segun Adebayo"
                                       />
                                       <Text
                                           fontSize={15}
                                           color={'gray.600'}
                                           fontWeight={'medium'}
                                       >Checkbox</Text>
                                   </VStack>
                               ))
                           }
                       </SimpleGrid>
                   </Flex>
                </Box>
                <Box flex={'3'} bg={'red'}>
                    hi
                </Box>
            </Flex>


        </Box>
    );
};

export default CreateQuestionPage;
