import dateFormat from "dateformat";

export const isOutDate = (date) =>{
    const now = new Date();
    if(now > date){
        return true;
    }
    return false;
}

export  const formatDateDisplay = (date)=>{
  return  dateFormat(date,"HH:mm dd-mm-yyyy");
}
