import React from 'react';
import {Box, Button, Center, Flex, Grid, GridItem, Input, Stack, Wrap, WrapItem} from "@chakra-ui/react";
import Card from "./card";
import Footer from "./footer";

const Home = () => {
    return (
        <Flex w={'100%'} direction={'column'}>
            {/*//Main Content*/}
            <Flex
                p={8}
                bg={'#F7FAFC'}
                minHeight={'100vh'}
                w={'100%'}
                direction={'column'}
                alignItems={'flex-start'}
            >
                <Box
                    minW={'800px'}
                >
                    <Stack boxShadow={'md'}
                           borderRadius={'10px'}
                           bg={'white'} p={'100px'}
                           direction={'row'}

                    >
                        <Input minH={'50px'} placeholder={'Enter a join code'}/>
                        <Button minH={'50px'}
                                bgGradient="linear(to-l, #7928CA, #FF0080)"
                                color={'white'}>Join</Button>
                    </Stack>
                </Box>
                {/*popular quiz*/}
             <Box minH={'100vh'}>
                 <Grid templateColumns="repeat(5, 1fr)"  templateRows="repeat(2, 1fr)" gap={6} h={'100%'}  row={2}>


                     {/*<Wrap w={'100%'} spacing={6} alignSelf={'center'}>*/}
                     {
                         [1, 1, 1, 2, 2, 2, 12, 1].map((item, i) => (
                             // <WrapItem key={i}>
                             <GridItem key={i} w="100%"  bg="blue.500">
                                 <Card/>
                             </GridItem>

                             // </WrapItem>
                         ))
                     }
                     {/*</Wrap>*/}
                 </Grid>
             </Box>

            </Flex>
            {/*    end of main content*/}
            <Footer/>
        </Flex>
    );
};

export default Home;
