import axiosClient from "../api/axiosClient";

const UserService = {

    updateAvt:async (file) =>{
        const url=`/member/users/me/avt`;
        return axiosClient.post(url,file,{
            headers:{
                "Authorization" : `Bearer ${localStorage.getItem("accessToken")}`
            }
        })
    }
}
export default UserService;
