import {
    Box,
    Button, Checkbox,
    Container,
    Heading,
    HStack,
    IconButton, Select,
    SimpleGrid,
    Spacer,
    Text,
    Tooltip,
    VStack
} from "@chakra-ui/react";
import React, {useContext, useEffect, useState} from "react";
import axios from "axios";
import {useParams} from "react-router-dom";
import {useAdmin} from "../../../hooks/useAuth";
import {AddIcon, CopyIcon, DeleteIcon, EditIcon} from "@chakra-ui/icons";
import {QuizEditContext} from "../../../providers/QuizEditProvider";
import {AiOutlineTag, BsPen} from "react-icons/all";

const QuizEditor = () => {

    const {id} = useParams();
    const {quiz, setQuiz} = useContext(QuizEditContext);
    const {setFullMode} = useAdmin();
    useEffect(() => {
        const fetchQuizDetails = async () => {
            const url = `http://localhost:8080/api/v1/admin/quiz-management/managed-quizzes/${id}`;
            try {
                const resp = await axios.get(url, {
                    headers: {
                        "Authorization": `Bearer ${localStorage.getItem("accessToken")}`
                    }
                })
                resp.status === 200 && setQuiz(resp.data);
                console.log(resp.data)
            } catch (e) {
                console.log("Failed to get quiz details with id " + id);
            }
        }
        fetchQuizDetails();
    }, [])
    return (
        <VStack align={'start'} w={'100%'} minH={'100vh'}>

            <HStack spacing={4} color={'white'}>
                <Button px={4} colorScheme={'teal'} leftIcon={<EditIcon/>}>
                    {quiz && quiz.title}
                </Button>
                <Button px={4} colorScheme={'linkedin'} leftIcon={<AddIcon/>}>New Question</Button>
                <Button px={4} colorScheme={'purple'} leftIcon={<EditIcon/>}>Quiz title</Button>
                <Button px={4} colorScheme={'messenger'} leftIcon={<EditIcon/>}>Quiz title</Button>
            </HStack>

        {/*    QUESTION*/}
            {
                quiz&&quiz.questions.map((question,index)=>(
                    <Box
                        key={index}
                        boxShadow={"sm"}
                        my={5}
                        borderRadius={"md"}
                        align={"start"}
                        textAlign={"start"}
                        h={"100%"}
                        bg={"white"}
                        maxW={"600px"}
                        minW={"600px"}
                    >
                        <HStack p={2} bg={"gray.200"}>
                            {/*QUESTION INDEX*/}
                            <Text>Question 1</Text>
                            <Spacer />
                            <Tooltip label={"edit"} color={"teal.200"}>
                                <IconButton icon={<BsPen />} />
                            </Tooltip>
                            <Tooltip color={"red.500"} label={"remove"}>
                                <IconButton
                                    // onClick={onDeleteQuestion}
                                    icon={<DeleteIcon />}
                                />
                            </Tooltip>
                            <Tooltip label={"duplicate"}>
                                <IconButton icon={<CopyIcon />} />
                            </Tooltip>
                        </HStack>
                        <VStack align={"start"} p={2} spacing={5}>
                            {/*QUESTION TITLE*/}
                            <Container maxW="3xl">
                                <Box maxW="3xl">
                                    <Text fontWeight={"medium"}>quasrtion</Text>
                                </Box>
                            </Container>
                            <SimpleGrid
                                w={"100%"}
                                align={"start"}
                                columns={2}
                                spacing={2}
                            >
                                <Container  maxW="xl" centerContent>
                                    <Box
                                        py={4}
                                        w={"100%"}
                                        maxW="3xl"
                                        align={"start"}
                                    >
                                        <Checkbox
                                            w={"100%"}
                                            size={"sm"}
                                            disabled={true}
                                            // isChecked={option.isChecked}
                                        >
                                            opioj
                                        </Checkbox>
                                    </Box>
                                </Container>
                            </SimpleGrid>
                        </VStack>
                        <HStack bg={"gray.200"} p={1}>
                            <Select
                                size={"sm"}
                                maxW={"100"}
                                bg={"white"}
                                name={"queston seconds"}
                            >
                                <option value="option1">30 secs</option>
                                <option value="option2">15 secs</option>
                                <option value="option3">45 secs</option>
                            </Select>
                            <Button
                                size={"sm"}
                                colorScheme={"white"}
                                variant={"ghost"}
                                leftIcon={<AiOutlineTag />}
                            >
                                Tag topics
                            </Button>
                        </HStack>
                    </Box>

                ))
            }
        </VStack>
    );
}

export default QuizEditor;
