import React, {useContext, useState} from 'react';
import {
    Box,
    Button,
    FormControl, FormHelperText,
    FormLabel,
    HStack,
    Input,
    Text,
    useRadio, useRadioGroup,
    VStack,
    Wrap,
    WrapItem
} from "@chakra-ui/react";
import {useHistory} from "react-router-dom";
import {useQuiz} from "../../context/QuizProvider";

const CreateQuizDialog = ({show,setShowCreateQuiz,onClose}) => {

    const [quizName, setQuizName] = useState('');
    const [subject, setSubject] = useState('');
    const [quizNameError, setQuizNameError] = useState(null);
    const [subjectError, setSubjectError] = useState(null);

    const history = useHistory();
    const {quizNew, createQuiz} = useQuiz();
    return (
        <>
            <Box
                bg={'gray.100'}
                // bg={'teal.500'}
                zIndex={1000}
                position={'fixed'}
                top={'50%'}
                boxShadow={'xl'}
                right={'50%'}
                align={'start'}
                borderRadius={'xl'}
                transform={
                    'translate(50%,-50%)'
                }
            >
                <VStack align={'start'} p={4} width={'500px'} maxW={['360px', '500px']}>
                    <HStack align={'start'}>
                        <Text fontSize={'20px'} fontWeight={'medium'}>Create a Quiz</Text>
                    </HStack>

                    <VStack color={'gray.600'} align={'start'} w={'100%'}>
                        <FormControl>
                            <FormLabel fontWeight={'normal'}>
                                1. Name this quiz
                            </FormLabel>
                            {/*1. quiz name*/}
                            <Input
                                onChange={(e) => {
                                    setQuizName(e.target.value);
                                    setQuizNameError(null);
                                }}
                                value={quizName}
                                isInvalid={quizNameError != null}
                                borderColor={'grey.600'}
                                isFullWidth={true}
                                width={'100%'}
                                variant="outline"/>
                            {
                                quizNameError && <FormHelperText color={'crimson'}>{quizNameError}</FormHelperText>
                            }
                        </FormControl>
                        {/*2. Choose a subject*/}
                        <VStack w={'100%'} align={'start'}>
                            <FormLabel fontWeight={'normal'}>
                                2. Choose revelant subjects
                            </FormLabel>
                            <RadioGroup setSubject={setSubject} setSubjectError={setSubjectError}/>
                            {subjectError != null
                            && <Text color={'red'} fontSize={12} fontStyle={'italic'}>Please select one subject</Text>
                            }
                        </VStack>
                        <HStack pt={5} w={'100%'} justify={'end'}>
                            <Button
                                onClick={() => {
                                    setShowCreateQuiz(false);
                                }}
                                colorScheme="gray" fontWeight={'normal'}>Cancel</Button>
                            <Button
                                colorScheme="teal"
                                fontWeight={'normal'}
                                onClick={() => {
                                    if (quizName == null || quizName.length === 0) {
                                        setQuizNameError("Please enter a quiz name");
                                        return;
                                    }
                                    if (subject == null || subject.length === 0) {
                                        setSubjectError('Please select a subject');
                                        return;
                                    }
                                    setShowCreateQuiz(false);

                                    createQuiz({
                                        ...quizNew,
                                        title: quizName,
                                        subject: subject,
                                    });
                                    onClose();
                                    // history.push("/admin/quiz");
                                }}
                            >
                                Next
                            </Button>
                        </HStack>
                    </VStack>


                </VStack>
            </Box>
        </>
    );
};

// 1. Create a component that consumes the `useRadio` hook
function CustomRadioCard(props) {
    const {getInputProps, getCheckboxProps} = useRadio(props)

    const input = getInputProps()
    const checkbox = getCheckboxProps()

    return (
        <Box as="label">
            <input {...input} />
            <Box
                fontSize={12}
                {...checkbox}
                cursor="pointer"
                borderWidth="1px"
                borderRadius="xl"
                // boxShadow="md"
                _checked={{
                    bg: "teal.600",
                    color: "white",
                    borderColor: "teal.600",
                }}
                _focus={{
                    boxShadow: "outline",
                }}
                // px={5}
                // py={3}
                p={.5}
            >
                {props.children}
            </Box>
        </Box>
    )
}

// Step 2: Use the `useRadioGroup` hook to control a group of custom radios.
function RadioGroup({setSubject, setSubjectError}) {
    const options = ["react", "vue", "svelte", 'asdsad', 'rere', 'sf34', '3434', 'sdfsf']

    const {getRootProps, getRadioProps} = useRadioGroup({
        name: "subject",
        defaultValue: "default",
        onChange: (e) => {
            setSubject(e);
            setSubjectError(null);
        },
    })

    const group = getRootProps()

    return (
        <Wrap {...group}>
            {options.map((value, i) => {
                const radio = getRadioProps({value})
                return (
                    <WrapItem key={i}>
                        <CustomRadioCard key={i} {...radio}>
                            {value}
                        </CustomRadioCard>
                    </WrapItem>
                )
            })}
        </Wrap>
    )
}


export default CreateQuizDialog;
