import {
    Box,
    Button, Checkbox,
    Container,
    Heading,
    HStack,
    IconButton, Select,
    SimpleGrid,
    Spacer, Stack,
    Text,
    Tooltip,
    VStack
} from "@chakra-ui/react";
import React, {useContext, useEffect, useState} from "react";
import axios from "axios";
import {useParams} from "react-router-dom";
import {useAdmin} from "../../../hooks/useAuth";
import {AddIcon, CopyIcon, DeleteIcon, EditIcon} from "@chakra-ui/icons";
import {QuizEditContext} from "../../../providers/QuizEditProvider";
import {AiOutlineTag, BsPen, MdArrowDropDown} from "react-icons/all";
import NewQuestionDialog from "./components/NewQuestionDialog";

const QuizEditor = () => {
    const {id} = useParams();
    const {quiz, setQuiz} = useContext(QuizEditContext);
    const [sort, setSort] = useState('row-reverse');
    const [showNewQuestionDialog, setShowNewQuestionDialog] = useState(false);
    const [size, setSize] = useState('full');
    useEffect(() => {
        //fetch quiz details
        const fetchQuizDetails = async () => {
            const url = `http://localhost:8080/api/v1/admin/quiz-management/managed-quizzes/${id}`;
            try {
                const resp = await axios.get(url, {
                    headers: {
                        "Authorization": `Bearer ${localStorage.getItem("accessToken")}`
                    }
                })
                resp.status === 200 && setQuiz(resp.data);
                console.log(resp.data)
            } catch (e) {
                console.log("Failed to get quiz details with id " + id);
            }
        }
        fetchQuizDetails();
    }, [])
    return (
        <>
            <VStack align={'start'} w={'100%'} minH={'100vh'}>

                <HStack spacing={4} color={'white'}>
                    <Button px={4} colorScheme={'teal'} leftIcon={<EditIcon/>}>
                        {quiz && quiz.title}
                    </Button>
                    <Button onClick={() => setShowNewQuestionDialog(true)} px={4} colorScheme={'linkedin'}
                            leftIcon={<AddIcon/>}>
                        New Question
                    </Button>
                    <Button px={4} colorScheme={'messenger'} leftIcon={<EditIcon/>}>
                        Quiz title
                    </Button>
                    <Select bg="tomato"
                            onChange={(e) => {
                                setSort(e.target.value);
                                const reversed = quiz.questions.reverse();
                                setQuiz((prevState) => ({
                                    ...prevState,
                                    questions: reversed,
                                }))
                                ;
                            }}
                            borderColor="tomato"
                            maxW={20} colorScheme={'teal'} icon={<MdArrowDropDown/>}
                            variant="filled"
                            placeholder="Sort by">
                        <option value={'newest'}>Newest</option>
                        <option value={'oldest'}>Oldest</option>
                    </Select>
                </HStack>

                {/*    QUESTIONS*/}
                <Stack direction={sort}>
                    {
                        quiz && quiz.questions.map((question, index) => (
                            <Box
                                key={index}
                                boxShadow={"sm"}
                                my={5}
                                borderRadius={"md"}
                                align={"start"}
                                textAlign={"start"}
                                h={"100%"}
                                bg={"white"}
                                maxW={"600px"}
                                minW={"600px"}
                            >

                                <HStack p={2} bg={"gray.200"}>
                                    {/*QUESTION INDEX*/}
                                    <Text>Question {index}</Text>
                                    <Spacer/>
                                    <Tooltip label={"edit"} color={"teal.200"}>
                                        <IconButton icon={<BsPen/>}/>
                                    </Tooltip>
                                    <Tooltip color={"red.500"} label={"remove"}>
                                        <IconButton
                                            // onClick={onDeleteQuestion}
                                            icon={<DeleteIcon/>}
                                        />
                                    </Tooltip>
                                    <Tooltip label={"duplicate"}>
                                        <IconButton icon={<CopyIcon/>}/>
                                    </Tooltip>
                                </HStack>
                                {/*QUESTION*/}
                                <VStack align={"start"} p={2} spacing={5}>
                                    {/*QUESTION TITLE*/}
                                    <Container maxW="3xl">
                                        <Box maxW="3xl">
                                            <Text fontWeight={"medium"}>{question.title}</Text>
                                        </Box>
                                    </Container>
                                    <SimpleGrid
                                        w={"100%"}
                                        align={"start"}
                                        columns={2}
                                        spacing={2}
                                    >
                                        {
                                            question.options.map((option, index) => (
                                                <Container key={index} maxW="xl" centerContent>
                                                    <Box
                                                        py={4}
                                                        w={"100%"}
                                                        maxW="3xl"
                                                        align={"start"}
                                                    >

                                                        <Checkbox
                                                            key={index}
                                                            w={"100%"}
                                                            size={"md"}
                                                            disabled={true}
                                                            // isChecked={option.isChecked}
                                                        >
                                                            {option.content}
                                                        </Checkbox>


                                                    </Box>
                                                </Container>
                                            ))
                                        }
                                    </SimpleGrid>
                                </VStack>
                                <HStack bg={"gray.200"} p={1}>
                                    <Select
                                        size={"sm"}
                                        maxW={"100"}
                                        bg={"white"}
                                        name={"queston seconds"}
                                    >
                                        <option value="option1">30 secs</option>
                                        <option value="option2">15 secs</option>
                                        <option value="option3">45 secs</option>
                                    </Select>
                                    <Button
                                        size={"sm"}
                                        colorScheme={"white"}
                                        variant={"ghost"}
                                        leftIcon={<AiOutlineTag/>}
                                    >
                                        Tag topics
                                    </Button>
                                </HStack>
                            </Box>

                        ))
                    }
                </Stack>
            </VStack>

            {showNewQuestionDialog &&
            <NewQuestionDialog size={size} setSize={setSize} visible={showNewQuestionDialog}
                               setVisible={setShowNewQuestionDialog}/>}
        </>
    );
}

export default QuizEditor;
