import React, {useEffect, useState} from 'react';
import {
    Box,
    Heading,
    HStack,
    Stack,
    Table,
    TableCaption,
    Tbody,
    Td,
    Text,
    Tfoot,
    Th,
    Thead,
    Tr
} from "@chakra-ui/react";
import axios from "axios";
import {useHistory} from "react-router-dom";
import {AiOutlineUser} from "react-icons/all";

const ReportPage = () => {
    const [reportData, setData] = useState(null);
    const history = useHistory();
    useEffect(async () => {
        const url = 'http://localhost:8080/api/v1/admin/quiz-assignment/assign-quizzes/report';
        try {
            const resp = await axios.get(url, {
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${localStorage.getItem('accessToken')}`
                }
            })
            console.log(resp.data);
            setData(resp.data);
        } catch (e) {
            console.log('Failed to get report data ', e)
        }
    }, []);
    return (
        <Box w={'100%'}>
            <Table boxShadow={'md'} variant="simple" colorScheme="blue" bg={'white'} p={4}>
                <TableCaption>Quiz Reporting</TableCaption>
                <Thead>
                    <Tr>
                        <Th>Quiz name</Th>
                        <Th>
                            <Text>Total participants</Text>
                        </Th>
                        <Th isNumeric>Avg score</Th>
                    </Tr>
                </Thead>
                <Tbody>
                    {
                        reportData && reportData.map((item) => {
                            const avgScore = () => {
                                let rs = 0;
                                item.participants.forEach((u) => u.score ? rs += u.score : 0);
                                return rs / (item.participants.length);
                            }
                            return <Tr
                                onClick={() => {
                                    history.push(`/admin/report/1732/details`)
                                }
                                }
                                cursor={'pointer'} key={item.title}>
                                <Td>{item.title}</Td>
                                <Td>{item.participants.length}</Td>
                                <Td isNumeric>{avgScore()}</Td>
                            </Tr>
                        })
                    }

                </Tbody>
                <Tfoot>
                    <Tr>
                        <Th>To convert</Th>
                        <Th>into</Th>
                        <Th isNumeric>multiply by</Th>
                    </Tr>
                </Tfoot>
            </Table>
        </Box>
    );
};

export default ReportPage;
