import React, {useEffect} from 'react';

import {
    Box,
    Center,
    useColorModeValue,
    Heading,
    Text,
    Stack,
    Image, Tag,
} from '@chakra-ui/react';

// const IMAGE =
//     'https://images.unsplash.com/photo-1518051870910-a46e30d9db16?ixlib=rb-1.2.1&ixid=MXwxMjA3fDB8MHxwaG90by1
const IMAGE =
    'https://cdn-icons-png.flaticon.com/512/4193/4193243.png';

const Card = ({quizInfo}) => {
    return (
        <Center cursor={'pointer'}>
            <Box
                boxShadow={'md'}
                role={'group'}
                p={2}
                maxW={'250px'}
                w={'full'}
                bg={useColorModeValue('white', 'gray.800')}
                // boxShadow={'2xl'}
                rounded={'lg'}
                pos={'relative'}
                zIndex={1}>
                {/*Image*/}
                <Box
                    rounded={'lg'}
                    // mt={-12}
                    pos={'relative'}
                    // height={'230px'}
                    // _after={{
                    //     transition: 'all .3s ease',
                    //     content: '""',
                    //     w: 'full',
                    //     h: 'full',
                    //     pos: 'absolute',
                    //     top: 5,
                    //     left: 0,
                    //     backgroundImage: `url(${IMAGE})`,
                    //     filter: 'blur(15px)',
                    //     zIndex: -1,
                    // }}
                    _groupHover={{
                        _after: {
                            filter: 'blur(20px)',
                        },
                    }}
                >
                    <Image
                        p={2}
                        rounded={'lg'}
                        height={200}
                        width={200}
                        objectFit={'cover'}
                        src={IMAGE}
                    />
                </Box>
                {/*End of Image*/}

                <Stack align={'center'} pb={2}>
                    {/*<Text color={'gray.500'} fontSize={'sm'} textTransform={'uppercase'}>*/}
                    {/*    Brand*/}
                    {/*</Text>*/}
                    <Tag>Brand</Tag>
                    <Heading fontSize={'sm'} fontFamily={'body'} fontWeight={500}>
                        {/*{quizInfo.title}*/}
                    </Heading>
                    {/*<Stack direction={'row'} align={'center'}>*/}
                    {/*    <Text fontWeight={800} fontSize={'xl'}>*/}
                    {/*        $57*/}
                    {/*    </Text>*/}
                    {/*    <Text textDecoration={'line-through'} color={'gray.600'}>*/}
                    {/*        $199*/}
                    {/*    </Text>*/}
                    {/*</Stack>*/}
                </Stack>

                {/*<Box bg={'red'} top={0} left={5} position={'absolute'}>*/}
                {/*    df*/}
                {/*</Box>*/}
            </Box>
        </Center>
    );
};
export default Card;
