import React, {useContext, useState} from 'react';
import {CKEditor} from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import {
    Box,
    Button,
    Container,
    Heading,
    HStack,
    Input, Modal, ModalBody, ModalCloseButton, ModalContent, ModalFooter, ModalHeader,
    ModalOverlay,
    Spacer,
    Text,
    Textarea, useDisclosure,
    VStack
} from "@chakra-ui/react";
import {BiFullscreen} from "react-icons/all";
import {QuizEditContext} from "../../../../providers/QuizEditProvider";

const NewQuestionDialog = ({visible, setVisible, size, setSize}) => {

    const {quiz, setQuiz} = useContext(QuizEditContext);

    const [question, setQuestion] = useState(null);
    const [options, setOptions] = useState([{}, {}]);

    const onOptionChange = (e, index) => {
        //push new option if it is the last index option
        if (index === options.length - 1) {
            options.push({});
        }
        setOptions((prevState) => (
            prevState.map((option, i) => i === index
                ? {
                    ...option,
                    content: e.target.value
                }
                : option)
        ));
        setQuestion((prevState) => ({
                ...prevState,
                options: options.filter(option => (option.content && option.content.length > 0))
            }
        ));
    }
    const onSaveOption = () => {
        if (!question || !question.title) {
            return;
        }
        if (options.length === 0) {
            return;
        }
        setQuestion((prevState) => ({
            ...prevState,
            options: options
        }))


        setQuiz((prevState) => ({
            ...prevState,
            questions: [...prevState.questions, question]
        }))
        setVisible(false);

    }
    return (
        <>
            <Modal onClose={() => setVisible(false)} size={size} isOpen={visible}>
                <ModalOverlay/>
                <ModalContent>
                    <ModalHeader>
                        <HStack>
                            <Text> New Question</Text>
                            <BiFullscreen onClick={() => size === 'full' ? setSize('xl') : setSize('full')}/>
                        </HStack>
                    </ModalHeader>
                    <ModalCloseButton/>
                    <VStack p={5} spacing={5} maxH={'900px'} overflowY={'scroll'}>

                        <VStack w={'100%'}>
                            <Text textAlign={'start'} w={'100%'} fontWeight={'medium'}>Question title</Text>

                            <Textarea
                                onChange={(e) => setQuestion((prevState) => ({...prevState, title: e.target.value}))}
                                borderColor={'black.800'} placeholder="Type your question title here"/>

                        </VStack>
                        <VStack w={'100%'}>
                            {
                                options.map((option, index) => (
                                    <Input
                                        onChange={(e) => onOptionChange(e, index)}
                                        key={index}
                                        placeholder={"option"}/>
                                ))
                            }
                        </VStack>

                    </VStack>

                    <ModalFooter>
                        <Button onClick={() => setVisible(false)}>Close</Button>
                        <Button ml={4} colorScheme={'blue'} onClick={onSaveOption}>Save</Button>
                    </ModalFooter>
                </ModalContent>
            </Modal>
            {/*<Box*/}
            {/*    onClick={() => {*/}
            {/*        setVisible(false)*/}

            {/*    }}*/}
            {/*    zIndex={1000}*/}
            {/*    bg={'gray.600'}*/}
            {/*    opacity={.5}*/}
            {/*    position={'fixed'}*/}
            {/*    right={0}*/}
            {/*    top={0}*/}
            {/*    minW={'100vw'}*/}
            {/*    minH={'100vh'}/>*/}
            {/*<Box zIndex={1001}*/}
            {/*     borderRadius={'md'}*/}
            {/*     boxShadow={'md'}*/}
            {/*     position={'fixed'}*/}
            {/*     right={'50%'}*/}
            {/*     top={'50%'}*/}
            {/*     bg={'gray.100'}*/}
            {/*     transform={"translate(50%,-50%)"}*/}
            {/*>*/}
            {/*    <VStack minW={800} p={5} spacing={5} maxH={'900px'} overflowY={'scroll'}>*/}
            {/*        <Text fontSize={'20'} fontWeight={'medium'} textAlign={'start'} w={'100%'}>Add questionss</Text>*/}
            {/*        <Box w={'100%'}>*/}
            {/*            <Textarea borderColor={'black'} placeholder="Here is a sample placeholder"/>*/}
            {/*        </Box>*/}
            {/*        <VStack w={'100%'}>*/}
            {/*            {*/}
            {/*                options.map((option, index) => (*/}
            {/*                    <Input*/}
            {/*                        onChange={(e) => onOptionChange(e, index)}*/}
            {/*                        key={index}*/}
            {/*                        isFullWidth={true} placeholder={"option"}/>*/}
            {/*                ))*/}
            {/*            }*/}
            {/*        </VStack>*/}
            {/*        <HStack w={'100%'}>*/}
            {/*            <Spacer/>*/}
            {/*            <Button variant={'outline'} colorScheme={'gray'}*/}
            {/*                    onClick={() => setVisible(false)}>Cancel</Button>*/}
            {/*            <Button variant={'solid'} colorScheme={'blue'}>Save</Button>*/}
            {/*        </HStack>*/}
            {/*    </VStack>*/}
            {/*</Box>*/}
        </>
    );
};

export default NewQuestionDialog;
