import React from "react";
import {
    Box,
    Center,
    Flex,
    Checkbox,
    Stack,
    Heading,
    Text,
    Button,
    Progress,
    Wrap,
    WrapItem,
} from "@chakra-ui/react";

const Quiz = () => {
    return (
        <Box w={"100%"}>
            <Center h={"100%"}>
                {/* Quiz Content */}
                <Flex
                    maxWidth={860}
                    w="100%"
                    direction={"column"}
                    alignContent="center"
                    alignItems="flex-start"
                >
                    {/* Quiz Title */}
                    <Text
                        m={4}
                        bgGradient="linear(to-l, #7928CA, #FF0080)"
                        bgClip="text"
                        fontSize="4xl"
                        fontWeight="extrabold"
                    >
                        Welcome to Chakra UI
                    </Text>
                    {/* End of Quiz Tile */}
                    {/* Buttons */}
                    <Wrap mb={4}>
                        <WrapItem>
                            <Center w="180px" h="80px" bg="red.200">
                                <Text
                                    fontSize="xl"
                                    fontWeight="medium"
                                    color="white"
                                >
                                    10:10 Left
                                </Text>
                            </Center>
                        </WrapItem>
                        <WrapItem>
                            <Center w="180px" h="80px" bg="green.200">
                                Box 2
                            </Center>
                        </WrapItem>
                        <WrapItem>
                            <Center w="180px" h="80px" bg="tomato">
                                Box 3
                            </Center>
                        </WrapItem>
                        <WrapItem>
                            <Center w="180px" h="80px" bg="blue.200">
                                Box 4
                            </Center>
                        </WrapItem>
                    </Wrap>
                    {/* End of Buttons */}
                    <Progress
                        mt={1}
                        mb={1}
                        w={"100%"}
                        value={50}
                        colorScheme="pink"
                        size="sm"
                        // isIndeterminate
                    />
                    {/* Questions */}
                    {[1, 2, 3, 4, 5].map((item, index) => (
                        <Box
                            // pb={5}
                            bg="#ffdde1"
                            mb={2}
                            mt={2}
                            w="100%"
                            p={4}
                            borderRadius={10}
                        >
                            {/* Title */}
                            <Text mb={3} fontSize="xl" fontWeight="medium">
                                {index + 1}. What is default template in java
                                spring?
                            </Text>
                            {/* Options Group */}
                            <Stack spacing={2} direction={"column"}>
                                <Checkbox
                                    colorScheme="purple"
                                    // iconColor="black"
                                    spacing={"1rem"}
                                    borderColor="white"
                                    value="html"
                                >
                                    HTML
                                </Checkbox>
                                <Checkbox
                                    colorScheme="purple"
                                    // iconColor="black"
                                    spacing={"1rem"}
                                    borderColor="white"
                                >
                                    JSP
                                </Checkbox>
                                <Checkbox
                                    colorScheme="purple"
                                    // iconColor="black"
                                    spacing={"1rem"}
                                    borderColor="white"
                                >
                                    Thymelaft
                                </Checkbox>
                                <Checkbox
                                    colorScheme="purple"
                                    // iconColor="black"
                                    spacing={"1rem"}
                                    borderColor="white"
                                >
                                    None of above
                                </Checkbox>
                            </Stack>
                        </Box>
                    ))}
                    <Box alignSelf="flex-end" mt={2}>
                        <Button colorScheme="blue" size="lg">
                            Submit
                        </Button>
                    </Box>
                </Flex>
                {/* End of Quiz */}
                <Box height={"5vh"}></Box>
            </Center>
        </Box>
    );
};

export default Quiz;
