import React, {useEffect, useState} from 'react';
import {
    Box,
    Heading,
    HStack, Menu, MenuButton, MenuDivider, MenuItem, MenuList,
    Stack,
    Table,
    TableCaption,
    Tbody,
    Td,
    Text,
    Tfoot,
    Th,
    Thead,
    Tr
} from "@chakra-ui/react";
import axios from "axios";
import {useHistory} from "react-router-dom";
import {AiOutlineUser} from "react-icons/all";
import {ChevronDownIcon} from "@chakra-ui/icons";

const ReportPage = () => {
    const [reportData, setData] = useState(null);
    const history = useHistory();
    useEffect(async () => {
        const url = 'http://localhost:8080/api/v1/admin/quiz-assignment/assign-quizzes/report';
        try {
            const resp = await axios.get(url, {
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${localStorage.getItem('accessToken')}`
                }
            })
            console.log(resp.data);
            setData(resp.data);
        } catch (e) {
            console.log('Failed to get report data ', e)
        }
    }, []);
    return (
        <Box w={'100%'}>
            <Table boxShadow={'md'} variant="simple" colorScheme="blue" bg={'white'} p={4}>
                <TableCaption>Quiz Reporting</TableCaption>
                <Thead color={'blue'}>
                    <Tr color={'blue'}>
                        <Th py={5} color={'teal'} fontSize={14}>Quiz name</Th>
                        <Th color={'teal'} fontSize={14}>
                            Total participants
                        </Th>
                        <Th color={'teal'} fontSize={14} isNumeric>Avg score</Th>
                        <Th/>
                    </Tr>
                </Thead>
                <Tbody>
                    {
                        reportData && reportData.map((item) => {
                            const avgScore = () => {
                                let rs = 0;
                                item.participants.forEach((u) => u.score ? rs += u.score : 0);
                                return rs / (item.participants.length);
                            }
                            return <Tr
                                cursor={'pointer'} key={item.title}>
                                <Td>{item.title}</Td>
                                <Td>{item.participants.length}</Td>
                                <Td isNumeric>{avgScore()}</Td>
                                <Td onClick={() => {
                                }}>
                                    <Menu>
                                        <MenuButton
                                            px={4}
                                            py={2}
                                            transition="all 0.2s"
                                            borderRadius="md"
                                            borderWidth="1px"
                                            _hover={{bg: "gray.400"}}
                                            _expanded={{bg: "blue.400"}}
                                            _focus={{boxShadow: "outline"}}
                                        >
                                            File <ChevronDownIcon/>
                                        </MenuButton>
                                        <MenuList>
                                            <MenuItem>New File</MenuItem>
                                            <MenuItem>New Window</MenuItem>
                                            <MenuDivider/>
                                            <MenuItem>Open...</MenuItem>
                                            <MenuItem>Save File</MenuItem>
                                        </MenuList>
                                    </Menu>
                                </Td>
                            </Tr>
                        })
                    }

                </Tbody>
                <Tfoot>
                    <Tr>
                        <Th>To convert</Th>
                        <Th>into</Th>
                        <Th isNumeric>multiply by</Th>
                    </Tr>
                </Tfoot>
            </Table>
        </Box>
    )
        ;
};

export default ReportPage;
