import React, {useRef, useState} from 'react';
import {
    Box,
    Button,
    Center, Checkbox, Container,
    Divider,
    Flex, FormControl, FormLabel,
    GridItem,
    Heading,
    HStack, IconButton,
    Image,
    SimpleGrid, Switch,
    Text, Tooltip,
    VStack
} from "@chakra-ui/react";
import {useParams} from "react-router-dom";
import {BiShare, BsSkipStart, FcStart} from "react-icons/all";
import SettingDialog from "./SettingDialog";
import {ArrowDownIcon, ArrowUpIcon, TimeIcon} from "@chakra-ui/icons";

const GradeQuiz = () => {

    //get id in params
    const {id} = useParams();
    const pageRef = useRef(null);
    const [showSetting, setShowSetting] = useState(false);

    return (
        <div ref={pageRef}>
            <Box
                // maxH={'50px'}
                align={'center'}
                position={'fixed'}
                maxW={200} top={5}
                right={'50%'}
                transform={
                    'translateX(50%)'
                }
                minW={'300px'}
                w={'100%'}
                bg={'red'}>
                <HStack spacing={5} p={5}>
                    <Button variant={'outline'} leftIcon={<TimeIcon/>}>23:00 left</Button>
                </HStack>
            </Box>
            <Box minH={'100vh'} p={10} bg={'gray.100'} align={'center'}>
                {/*<Box minH={'50px'}></Box>*/}
                {showSetting && <SettingDialog setShow={setShowSetting}/>}
                <Box bg={'white'}
                     borderRadius={'md'}
                     boxShadow={'xl'}
                     minH={'100vh'}
                     maxW={'860px'}
                     textAlign={'start'} p={5}>
                    <VStack>
                        {/*Question*/}
                        {
                            [1, 2, 3, 4, 3, 2, 1, 3].map((item, i) => (
                                <VStack key={i} align={'start'} p={2} spacing={5}>
                                    {/*    Question title*/}
                                    <Container maxW="3xl">
                                        <Box maxW="3xl">
                                            <Text fontWeight={'medium'}>{i + 1}. This is a question</Text>
                                        </Box>
                                    </Container>
                                    {/*options*/}
                                    <SimpleGrid columns={2} spacing={2}>
                                        {
                                            [1, 2, 3, 4].map((item, index) => (
                                                <Container key={index} maxW="xl" centerContent>
                                                    <Box padding="1" maxW="3xl">
                                                        <Checkbox size={'sm'}>
                                                            There are many benefits to a joint design and
                                                            development system. Not only
                                                            does it bring benefits to the design team.
                                                        </Checkbox>
                                                    </Box>
                                                </Container>
                                            ))
                                        }
                                    </SimpleGrid>
                                    <Divider/>
                                </VStack>
                            ))
                        }
                        {/* Submit button*/}
                        <Box py={5} align={'end'} alignSelf={'end'}>
                            <Button alignSelf={'end'} size={'lg'} colorScheme={'teal'}>Submit</Button>
                        </Box>
                    </VStack>
                </Box>

                {/*Fixed action buttons*/}
                <HStack zIndex={100} position={'fixed'} bottom={0} right={0} m={10}>
                    <Tooltip label={"Scroll to top"}>
                        <IconButton
                            onClick={() => window.scrollTo(0, 0)}
                            colorScheme={'teal'}
                            icon={<ArrowUpIcon/>}/>
                    </Tooltip>
                    <Tooltip label={"Scroll to bottom"}>
                        <IconButton
                            onClick={() => {
                                pageRef.current.scrollIntoView({behavior: "smooth", block: "end", inline: "nearest"})
                            }}
                            colorScheme={'teal'}
                            icon={<ArrowDownIcon/>}/>
                    </Tooltip>

                </HStack>
            </Box>

        </div>
    );
};

export default GradeQuiz;
