import React from 'react';
import {Box, Button, Center, Flex, Grid, GridItem,SimpleGrid, Input, Stack, Wrap, WrapItem} from "@chakra-ui/react";
import Card from "./card";
import Footer from "./footer";

const Home = () => {
    return (
        <Flex w={'100%'} direction={'column'}>
            {/*//Main Content*/}
            <Flex
                p={8}
                bg={'#F7FAFC'}
                minHeight={'100vh'}
                w={'100%'}
                direction={'column'}
                alignItems={'flex-start'}
            >
                <Box
                    minW={'800px'}
                >
                    <Stack boxShadow={'md'}
                           borderRadius={'10px'}
                           bg={'white'} p={'100px'}
                           direction={'row'}

                    >
                        <Input minH={'50px'} placeholder={'Enter a join code'}/>
                        <Button minH={'50px'}
                                bgGradient="linear(to-l, #7928CA, #FF0080)"
                                color={'white'}>Join</Button>
                    </Stack>
                </Box>

                <SimpleGrid h='100%' columns={5} spacingX="40px" spacingY="20px">
                    {
                        [1,1,2,3,4,3,2,34,2,3,2,23,2].map((item,i)=>(
                            <Box key={i} w={'100%'} bg="tomato" height="80px">
                                <Card/>
                            </Box>

                        ))
                    }
                </SimpleGrid>

            </Flex>
            {/*    end of main content*/}
            <Footer/>
        </Flex>
    );
};

export default Home;
