import React from "react";
import {
    Box,
    Center,
    Flex,
    Checkbox,
    Stack,
    Heading,
    Text,
    Button,
    Progress,
    Wrap,
    WrapItem,
} from "@chakra-ui/react";

const Quiz = ({ quiz }) => {
    return (
        <Box w={"100%"}>
            <Center h={"100%"}>
                {/* Quiz Content */}
                <Flex
                    maxWidth={860}
                    w="100%"
                    direction={"column"}
                    alignContent="center"
                    alignItems="flex-start"
                >
                    {/* Quiz Title */}
                    <Text
                        m={4}
                        bgGradient="linear(to-l, #7928CA, #FF0080)"
                        bgClip="text"
                        fontSize="4xl"
                        fontWeight="extrabold"
                    >
                        {quiz != null && quiz.title}
                    </Text>
                    {/* End of Quiz Tile */}
                    {/* Buttons */}
                    <Wrap mb={4}>
                        <WrapItem>
                            <Center w="180px" h="80px" bg="red.200">
                                <Text
                                    fontSize="xl"
                                    fontWeight="medium"
                                    color="white"
                                >
                                    10:10 Left
                                </Text>
                            </Center>
                        </WrapItem>
                        <WrapItem>
                            <Center w="180px" h="80px" bg="green.200">
                                Box 2
                            </Center>
                        </WrapItem>
                        <WrapItem>
                            <Center w="180px" h="80px" bg="tomato">
                                Box 3
                            </Center>
                        </WrapItem>
                        <WrapItem>
                            <Center w="180px" h="80px" bg="blue.200">
                                Box 4
                            </Center>
                        </WrapItem>
                    </Wrap>
                    {/* End of Buttons */}
                    <Progress
                        mt={1}
                        mb={1}
                        w={"100%"}
                        value={50}
                        colorScheme="pink"
                        size="sm"
                        // isIndeterminate
                    />
                    {/* Quiz */}
                    {quiz != null &&
                        quiz.questions.map((question, qIndex) => (
                            <Box
                                key={qIndex}
                                // pb={5}
                                bg="#ffdde1"
                                mb={2}
                                mt={2}
                                w="100%"
                                p={4}
                                borderRadius={10}
                            >
                                {/* Title */}
                                <Text mb={3} fontSize="xl" fontWeight="medium">
                                    {qIndex + 1}. {question.title}
                                </Text>
                                {/* Options Group */}
                                <Stack spacing={2} direction={"column"}>
                                    {question.options.map((option, index) => (
                                        <Checkbox
                                            colorScheme="purple"
                                            // iconColor="black"
                                            spacing={"1rem"}
                                            borderColor="white"
                                            value="html"
                                        >
                                            {option.content}
                                        </Checkbox>
                                    ))}
                                </Stack>
                            </Box>
                        ))}
                    <Box alignSelf="flex-end" mt={2}>
                        <Button colorScheme="blue" size="lg">
                            Submit
                        </Button>
                    </Box>
                    <Box h="70px" />
                </Flex>
                {/* End of Quiz */}
            </Center>
        </Box>
    );
};

export default Quiz;
