import React, {useContext, useEffect, useRef, useState} from 'react';
import {Box, Button, Divider, Flex, Heading, HStack, Icon, SimpleGrid, Text} from "@chakra-ui/react";
import UserService from "../../../service/UserService";
import {useHistory, useParams} from "react-router-dom";
import {AdminContext} from "../../../providers/AdminSettingProvider";
import {formatDateDisplay, isOutDate} from "../../../utils/ApplicationUtils";
import {GrInfo} from 'react-icons/all';
import {
    AlertDialog,
    AlertDialogBody,
    AlertDialogFooter,
    AlertDialogHeader,
    AlertDialogContent,
    AlertDialogOverlay,
} from '@chakra-ui/react'

const AssignedInfo = () => {
    const {isLoading, setLoading} = useContext(AdminContext);
    const {id} = useParams();

    const [info, setInfo] = useState(null);
    useEffect(() => {
        getInfo();
    }, []);

    const history = useHistory();
    //alert dialog properties
    const [isOpen, setIsOpen] = useState(false)
    const onClose = () => setIsOpen(false)
    const cancelRef = useRef()

    const getInfo = async () => {
        try {
            setLoading(true)
            const resp = await UserService.getAssignedQuizInfo(id);
            console.log(resp.data);
            setInfo(resp.data);
        } catch (e) {

        } finally {
            setLoading(false)
        }
    }
    const onStartQuiz = async () => {
        if (!isLoading) {

            const startDate = new Date(info.startDate);
            const finishDate = new Date(info.finishDate);
            const now = new Date();
            if (now >= startDate && now < finishDate) {
                history.push(`/admin/join/exam/quiz/${info.quiz.code}/start`);
                console.log('trong khoang thoi gian thuc hien');
            } else {
                console.log('nam ngoai khoang thoi gian thuc hien')
            }
            setIsOpen(true);
        }

    }
    return (
        <Flex direction={'column'}>
            <Flex>
                <Flex bg={'white'} p={'5'} direction={'column'} flex={7}>
                    <Heading>TITLE</Heading>
                    <HStack>
                        <Text>{info && info.title}</Text>
                        <Text>{formatDateDisplay(info && info.startDate)}</Text>
                    </HStack>
                    <Text>{info && info.description}</Text>
                    <Divider py={'2'}/>
                    <Flex py={2} justifyContent={'end'}>
                        <Text>Den han</Text>
                        <Box w={'20px'}/>
                        <Text>{formatDateDisplay(info && info.finishDate)}</Text>
                    </Flex>
                </Flex>
                <Box w={'20px'}/>
                {!isLoading && info &&
                <Flex bg={'white'} p={'5'} direction={'column'} justifyContent={'space-between'} flex={3}>
                    <Flex justifyContent={"space-between"}>
                        <Text>Status</Text>
                        <Text
                            fontWeight={'medium'}>{info.status === 1 ? 'Da nop'
                            : (info.status === 0 && isOutDate(info.finishDate) ? 'Thieu' : 'Da giao')}</Text>
                    </Flex>
                    <Box w={'100%'}>
                        <Button
                            onClick={onStartQuiz}
                            disabled={info.status == 1 || isOutDate(info.finishDate)}
                            colorScheme={'blue'} w={'100%'}>Start</Button>
                    </Box>
                </Flex>

                }
            </Flex>
            {
                !isLoading && info && (
                    <Flex p={'5'} bg={'white'} my={5} direction={'column'}>
                        <Heading>
                            {info.quiz.title}
                        </Heading>
                        <SimpleGrid columns={3} spacing={5}>
                            <HStack>
                                <Icon as={GrInfo}/>
                                <Text>{info.quiz.numOfQuestions} Questions </Text>
                            </HStack>
                            <HStack>
                                <Icon as={GrInfo}/>
                                <Text> Code : {info.quiz.code} </Text>
                            </HStack>
                            <HStack>
                                <Icon as={GrInfo}/>
                                <Text>Create Date {formatDateDisplay(info.quiz.createDate)}  </Text>
                            </HStack>
                            <HStack>
                                <Icon as={GrInfo}/>
                                <Text>Quiz Score {info.quiz.score}  </Text>
                            </HStack>
                        </SimpleGrid>
                    </Flex>
                )
            }
            <AlertDialog
                isOpen={isOpen}
                leastDestructiveRef={cancelRef}
                onClose={onClose}
            >
                <AlertDialogOverlay>
                    <AlertDialogContent>
                        <AlertDialogHeader fontSize='lg' fontWeight='bold'>
                            Delete Customer
                        </AlertDialogHeader>

                        <AlertDialogBody>
                            Are you sure? You can't undo this action afterwards.
                        </AlertDialogBody>

                        <AlertDialogFooter>
                            <Button ref={cancelRef} onClick={onClose}>
                                Cancel
                            </Button>
                            <Button colorScheme='red' onClick={onClose} ml={3}>
                                Delete
                            </Button>
                        </AlertDialogFooter>
                    </AlertDialogContent>
                </AlertDialogOverlay>
            </AlertDialog>
        </Flex>
    );
};

export default AssignedInfo;
