import React from 'react';
import {
    Box,
    Button,
    Center, Checkbox, Container,
    Flex,
    HStack,
    IconButton,
    Image,
    Input,
    SimpleGrid,
    Spacer, Stack,
    Text,
    VStack
} from "@chakra-ui/react";
import {AddIcon, ArrowBackIcon, CopyIcon, DeleteIcon} from "@chakra-ui/icons";

const QuizCreatePage = () => {
    return (
        <Box bg={'#F7FAFC'}>
            {/*  Header with action bar*/}
            <Box bg={'red'} position={'fixed'} top={0} w={'100%'} bg={'#F7FAFC'} >
                <Box bg={'red'} maxH={'60px'} bg={'teal.600'}>
                    <Flex align={'center'} p={2} alignItems={'center'} justifyContent={'space-between'}>
                        <ArrowBackIcon fontSize={35}/>
                        <Text>Editted 1 minute ago</Text>
                        <Button>Publish</Button>
                    </Flex>
                </Box>
                <Box bg={'#F7FAFC'}   maxW={'860px'}>
                    <Box>
                        <Center>
                            <HStack boxShadow={'md'}  p={8}  borderBottomRadius={'xl'}spacing={4} bg={'white'}>
                                <VStack w={'400px'}>
                                    <Text alignSelf={'start'}>Search</Text>
                                    <Input isFullWidth={true}/>
                                </VStack>
                                <VStack align={'end'} w={'200px'}>
                                    <Text>Create question</Text>
                                    <Button size={'md'} rightIcon={<AddIcon/>} variant={'solid'} colorScheme={'teal'}>Add
                                        question</Button>
                                </VStack>
                            </HStack>
                        </Center>
                    </Box>
                </Box>

            </Box>
            {/*   Content with right sidebar*/}
            <Box pt={'200px'} minH={'100vh'} bg={'#F7FAFC'}>
                <Flex direction={'row'}>
                    {/*Grid*/}
                    <Box mt={10} w={'100%'} maxW={'860px'}>
                        <Center>
                            {/*<SimpleGrid*/}
                            {/*    boxShadow={'md'}*/}
                            {/*    h={'100%'}*/}
                            {/*    p={10}*/}
                            {/*    bg={'white'}*/}
                            {/*    align={'center'}*/}
                            {/*    justifyContent={'center'}*/}
                            {/*    justifyItems={'center'}*/}
                            {/*    minChildWidth="100px"*/}
                            {/*    maxW={'500px'}*/}
                            {/*    spacing={2}>*/}
                            {/*    {*/}
                            {/*        [1, 2, 2, 2, 2, 2].map((item, index) => (*/}
                            {/*            <VStack cursor={'pointer'} key={index}>*/}
                            {/*                <Image*/}
                            {/*                    boxSize="80px"*/}
                            {/*                    objectFit="cover"*/}
                            {/*                    src="https://cdn-icons-png.flaticon.com/512/1168/1168671.png"*/}
                            {/*                    alt="Segun Adebayo"*/}
                            {/*                />*/}
                            {/*                <Text*/}
                            {/*                    fontSize={15}*/}
                            {/*                    color={'gray.600'}*/}
                            {/*                    fontWeight={'medium'}*/}
                            {/*                >Checkbox</Text>*/}
                            {/*            </VStack>*/}
                            {/*        ))*/}
                            {/*    }*/}
                            {/*</SimpleGrid>*/}
                         <Stack w={'100%'} direction={'column'}>
                             {
                                 [1,2,2,2,2,2,].map((item,i)=>(
                                     <Box key={i} my={5} borderRadius={'md'} align={'start'} textAlign={'start'} h={'100%'} bg={'teal'} maxW={'600px'}
                                          minW={'600px'}>
                                         {/*Edit bar*/}
                                         <HStack   p={2} bg={'gray.200'}>
                                             <Text>Question1</Text>
                                             <Spacer/>
                                             <Button>Edit</Button>
                                             <IconButton icon={<DeleteIcon/>}/>
                                             <IconButton icon={<CopyIcon/>}/>
                                         </HStack>
                                         <VStack align={'start'} p={2} spacing={5}>
                                             {/*    Question title*/}
                                             <Text>This is question 1</Text>
                                             <SimpleGrid  columns={2} spacing={2}>
                                                 {
                                                     [1,2,3,4].map((item,index)=>(
                                                         // <Container key={index} maxW="container.lg">
                                                         //     <Checkbox size={'md'}>
                                                         //         This is question1asdfffffffffffffffffffffffffffffffffff
                                                         //     </Checkbox>
                                                         // </Container>

                                                         <Container maxW="xl" centerContent>
                                                             <Box padding="1"  maxW="3xl">
                                                                 <Checkbox size={'sm'}>
                                                                     There are many benefits to a joint design and development system. Not only
                                                                     does it bring benefits to the design team.
                                                                 </Checkbox>

                                                             </Box>
                                                         </Container>
                                                     ))
                                                 }


                                             </SimpleGrid>
                                         </VStack>
                                     </Box>
                                 ))
                             }
                         </Stack>
                        </Center>
                    </Box>

                </Flex>

            </Box>
        </Box>
    );
};

export default QuizCreatePage;
