import React, {useState} from 'react';
import {
    Box,
    Button,
    Center,
    Checkbox,
    Container,
    HStack,
    Input,
    Select,
    Text,
    Textarea,
    VStack
} from "@chakra-ui/react";

const EditQuestionDialog = ({setVisible}) => {


    const [numOfQuestions, setNumOfQuestion] = useState([0, 1, 2, 3]);

    // const [questions, setQuestions] = useState(['', '', '', '']);

    const [questions, setQuestions] = useState({
        title: '',
        options: ['', '', '', '']
    })

    //on title change
    const onQuizTitleChange = (e) => {
        setQuestions(prevState => ({
            ...prevState,
            title: e.target.value
        }))
    }

    const onOptionChange = (e, i) => {
        setQuestions(prevState => ({
            ...prevState,
            options: prevState.options.map((savedOption, index) => {
                if (i === index) {
                    return e.target.value;
                }
                return savedOption;
            })
        }))
    }

    const onAddOption = () => {
        setQuestions(prevState => ({
            ...prevState,
            title: prevState.title,
            options: [...prevState.options, '']
        }))
    }
    return (
        <>
            <Box
                onClick={() => {
                    console.log("HAHHAHA")
                }}
                position={'fixed'}
                zIndex={9000}
                top={0}
                bg={'gray.300'}
                align={'center'}
                alignItems={'center'}
                opacity={.4}
                justifyContent={'center'}
                width={'100vw'}
                height={'100vh'}>
            </Box>

            <Box boxShadow={'md'}
                 position={'fixed'}
                 zIndex={9001}
                 top={'50%'}
                 right={'50%'}
                 transform={
                     'translate(50%,-50%)'
                 }

                 minW={'500px'}
                 bg={'white'}
                 maxW={'500px'}
            >
                <VStack>
                    <HStack bg={'gray.700'} p={2} w={'100%'}>
                        <Text color={'white'} fontWeight={'medium'}>Question 2</Text>
                    </HStack>
                    {/*Question input title*/}
                    <Box p={2} w={'100%'}>
                        <Textarea
                            onChange={(e) => onQuizTitleChange(e)}
                            value={questions.title}
                            css={
                                {
                                    '&::-webkit-scrollbar': {
                                        width: '0px',
                                        background: 'transparent'
                                    }
                                }
                            }
                            resize={'none'}
                            overflow={'hidden'}
                            placeholder={'Type your question here'}/>
                    </Box>
                    <VStack p={4} spacing={4} w={'100%'}>
                        {/*{*/}
                        {/*    questions.map((item, i) => (*/}
                        {/*        <HStack key={i} w={'100%'}>*/}
                        {/*            <Checkbox/>*/}
                        {/*            <Input*/}
                        {/*                onChange={(e) => {*/}
                        {/*                    let updateState = questions*/}
                        {/*                        .map((savedQuestion, index) => {*/}
                        {/*                            if (i === index) {*/}
                        {/*                                return savedQuestion = e.target.value;*/}
                        {/*                            }*/}
                        {/*                            return savedQuestion;*/}
                        {/*                        })*/}
                        {/*                    setQuestions(updateState);*/}
                        {/*                }}*/}
                        {/*                wordBreak={'break-all'}*/}
                        {/*                wordwrap={'break-word'}*/}
                        {/*                isFullWidth={true}*/}
                        {/*                size={'sm'}/>*/}
                        {/*        </HStack>*/}
                        {/*    ))*/}
                        {/*}*/}
                        {
                            questions.options.map((item, i) => (
                                <HStack key={i} w={'100%'}>
                                    <Checkbox/>
                                    <Input
                                        value={questions.options[i]}
                                        onChange={(e) => onOptionChange(e, i)
                                            // let updateState = questions
                                            //     .map((savedQuestion, index) => {
                                            //         if (i === index) {
                                            //             return savedQuestion = e.target.value;
                                            //         }
                                            //         return savedQuestion;
                                            //     })
                                            // setQuestions(updateState);

                                        }
                                        wordBreak={'break-all'}
                                        wordwrap={'break-word'}
                                        isFullWidth={true}
                                        size={'sm'}/>
                                </HStack>
                            ))
                        }

                    </VStack>
                    {/*BUTTON ADD ANSWER*/}
                    <Box align={'start'} p={2} w={'100%'}>
                        <Button
                            // onClick={() => {
                            //     setQuestions(prev => [...prev, '']);
                            // }}

                            onClick={onAddOption}
                            size={'sm'}
                            variant={'outline'}
                            alignSelf={'start'}
                            colorScheme={'teal'}>
                            Add answer option
                        </Button>
                    </Box>
                    {/*BUTTON SAVE*/}
                    <Box w={'100%'}>
                        <HStack p={2} bg={'gray.700'} justifyContent={'end'}>
                            <Button onClick={() => {
                                setVisible(false)
                            }} size={'sm'}>Cancel</Button>
                            <Button onClick={() => {
                                setVisible(false)
                            }} size={'sm'}>Save</Button>
                        </HStack>
                    </Box>
                </VStack>
            </Box>
        </>

    );
};

export default EditQuestionDialog;
