import React, {useState} from 'react';
import {Box, Button, ButtonGroup, CircularProgress, Flex, Heading, HStack, Text, VStack} from "@chakra-ui/react";
import {AiOutlineFieldNumber, RiNumbersFill, RiNumbersLine} from "react-icons/all";
import {TimeIcon} from "@chakra-ui/icons";
import {useHistory} from "react-router-dom";
import axios from "axios";
import {useQuiz} from "../../../context/QuizProvider";

const FoundQuiz = () => {
    const [isLoading, setLoading] = useState(false);
    const history = useHistory();
    const {foundedQuiz, setQuestions} = useQuiz();

    const onStart = async () => {
        setLoading(true);
        try {
            const resp = await axios.get(`http://localhost:8080/api/v1/public/quiz/${foundedQuiz.code}/questions`);
            if (resp.status === 200) {
                setQuestions(resp.data);
            }
        } catch (e) {

        } finally {
            setLoading(false);
        }

    }
    const onCancel = () => {
        history.goBack();
    }

    return (
        <Box bg={'blue.100'} minH={'100vh'}>
            <Box position={'fixed'}
                 top={'50%'}
                 right={'50%'}
                 minH={'100vh'}
                 minW={'100vw'}
                 zIndex={900}
                 opacity={0.5}
                 bg={'gray.100'}
                 transform={"translate(50%,-50%)"}/>
            <CircularProgress
                position={'fixed'}
                top={'50%'}
                right={'50%'}
                isIndeterminate
                zIndex={901}
                transform={"translate(50%,-50%)"}
                value={30}
                color="orange.400" thickness="12px"/>

            <Flex w={'100%'} direction={'column'}>
                <Box>
                    <VStack pt={10} spacing={4}>
                        {/*    OPTIONS*/}
                        <Box w={500} minW={200} bg={'white'} p={10} textAlign={'start'}>
                            <VStack alignItems={'start'} textAlign={'start'}>
                                <Heading fontSize={20}> Question title</Heading>
                                <HStack spacing={5}>
                                    <Button leftIcon={<RiNumbersLine/>}>
                                        <Text>10 question</Text>
                                    </Button>
                                    <Button leftIcon={<TimeIcon/>}>
                                        <Text>30 mins </Text>
                                    </Button>
                                </HStack>
                            </VStack>
                        </Box>
                        {/*  QUIZ TITLE*/}

                        {/*<Heading>dddd</Heading>*/}
                        {/*  ACTION BUTTONS*/}
                        <VStack w={500} p={10} bg={'white'} minW={'200px'} spacing={8}>
                            <Button onClick={onStart} size={'lg'} w={'100%'} colorScheme={'teal'}>Start</Button>
                            <Button size={'lg'} w={'100%'} colorScheme={'blue'}>Share</Button>
                            <Button onClick={onCancel} size={'lg'} w={'100%'} colorScheme={'gray'}>Cancel</Button>
                        </VStack>
                    </VStack>
                </Box>
            </Flex>
        </Box>
    );
};

export default FoundQuiz;
