import React from 'react';
import {
    Box,
    Button,
    Center,
    Flex,
    Grid,
    GridItem,
    SimpleGrid,
    Input,
    Stack,
    Wrap,
    WrapItem,
    Heading, Text, HStack, Avatar, VStack
} from "@chakra-ui/react";
import Card from "./card";
import Footer from "../footer";

import {GoFlame} from "react-icons/all";

const HomeScreen = () => {
    return (
        <Flex direction={'column'}>
            {/*//Main Content*/}
            <Flex
                p={8}
                bg={'#F7FAFC'}
                // minHeight={'100vh'}
                w={'100%'}
                direction={'column'}
                alignItems={'flex-start'}
            >
                <Flex w={'100%'} justifyContent={'space-around'} alignItems={'center'}>
                    {/*Join Code*/}
                    <Box
                        // minW={'800px'}
                        w={['100px', '500px', '800px']}
                    >
                        <Stack boxShadow={'md'}
                               borderRadius={'10px'}
                               bg={'white'}
                               p={['10px', '50px', '100px']}
                               minW={'360px'}
                               direction={'row'}

                        >
                            <Input minH={'50px'} minW={'260px'} placeholder={'Enter a join code'}/>
                            <Button minH={'50px'} minW={'30px'}
                                    bgGradient="linear(to-l, #7928CA, #FF0080)"
                                    color={'white'}>Join</Button>
                        </Stack>
                    </Box>
                    {/*Day Streak*/}
                    <Box
                        boxShadow={'md'}
                        borderRadius={'10px'}
                        bg={'white'}
                        p={['10px',  '50px']}
                        align={'center'}>
                        <Stack direction={'column'}>
                            <Box bg={'#CBD5E0'} p={1} borderRadius={'200px'} minW={'200px'}>
                                <HStack>
                                    <Avatar name="Dan Abrahmov" src="https://bit.ly/dan-abramov" />
                                    <Text fontSize={'16px'} color={'#4A5568'} fontWeight={'medium'}>Nguyen Duy</Text>
                                </HStack>
                            </Box>
                            <HStack spacing={2} pt={1} pb={1}>
                                <GoFlame color={'red'} fontSize={'30px'}/>
                                <GoFlame color={'red'} fontSize={'30px'}/>
                                <GoFlame color={'#718096'} fontSize={'30px'}/>
                                <GoFlame color={'#718096'} fontSize={'30px'}/>
                                <GoFlame color={'#718096'} fontSize={'30px'}/>
                            </HStack>
                            <Text color={'#4A5568'} fontWeight={'bold'} fontSize={'16px'}>0 day streak</Text>
                            <Text color={'#718096'} fontSize={'14px'}>Start your streak, play now!</Text>
                        </Stack>
                    </Box>
                </Flex>

            </Flex>
            <Box p={8} minH={'100vh'} bg={'red'} align={'center'}>
                <VStack align={'start'}>
                    <Heading>Recent Activity</Heading>
                </VStack>
            </Box>
            <Footer/>

            {/*    end of main content*/}
        </Flex>
    );
};

export default HomeScreen;
