import React, {useEffect, useState} from 'react';
import {Box, Flex, Heading, HStack, IconButton, Image, ListIcon, Text, VStack} from "@chakra-ui/react";
import {AddIcon, DeleteIcon, EditIcon} from "@chakra-ui/icons";
import {BiShare, BsListCheck, BsPlay} from "react-icons/all";
import ShareDialog from "../../../components/admin/setting/ShareDialog";
import quizApi from "../../../api/quizApi";
import dateFormat from "dateformat";

const LibraryPage = () => {
    const [share, setShare] = useState(false);

    const [quizzes, setQuizzes] = useState([]);

    useEffect(() => {

        const getCreatedQuizzesInfo = async () => {
            try {
                const data = await quizApi.getCreatedQuizzesInfo();
                setQuizzes(data);
                console.log(data);
            } catch (e) {
                console.log("Failed to fetch created quizzes info");
            }

        }
        getCreatedQuizzesInfo();
    }, []);

    return (
        <>
            <Flex w={'100%'} bg={'gray.100'} direction={'column'}>
                {/*CREATED QUIZZES*/}
                {
                    quizzes && quizzes.map((item, i) => (
                        <Box my={2} key={i} p={5} minW={'100'} bg={'white'} maxW={600} position={'relative'}>
                            <HStack spacing={5}>
                                {/*IMAGE*/}
                                <Box boxSize="100px">
                                    <Image
                                        src="https://cdn-icons-png.flaticon.com/512/1205/1205526.png"
                                        alt="Q"/>
                                </Box>
                                {/*INFO*/}
                                <VStack align={'start'}>
                                    {/*STATUS*/}
                                    <HStack>
                                        <Text color={'gray.500'} fontSize={12}>Quiz</Text>
                                        <Text bg={'teal.100'} px={2} color={'gray.500'} fontSize={12}>Draft</Text>
                                    </HStack>
                                    {/*TITLE*/}
                                    <Text fontWeight={'medium'} fontSize={18}>{item.title}</Text>
                                    
                                    <HStack spacing={4}>
                                        <HStack>
                                            <IconButton size={"xs"} icon={<BsListCheck/>}/>
                                            <Text fontSize={12}>{item.numOfQuestions} Q</Text>
                                        </HStack>
                                        <HStack>
                                            <IconButton size={"xs"} icon={<BsPlay/>}/>
                                            <Text fontSize={12}>Played 0 times</Text>
                                        </HStack>
                                    </HStack>
                                    <Text>{dateFormat(item.createdDate, "dddd, mmmm, yyyy, h:MM:ss TT")}</Text>
                                </VStack>
                            </HStack>
                            <HStack spacing={5} position={'absolute'} top={5} right={5}>
                                <IconButton size={'sx'} icon={<EditIcon/>}/>
                                <IconButton size={'sx'} icon={<DeleteIcon/>}/>
                            </HStack>
                            <IconButton
                                onClick={() => {
                                    setShare(true);
                                }}
                                position={'absolute'} bottom={10} right={5} size={'sx'} icon={<BiShare/>}/>
                        </Box>
                    ))
                }
            </Flex>

            {
                share && <ShareDialog setVisible={setShare}/>
            }
        </>
    );
};

export default LibraryPage;
