import React, {useState} from 'react';
import {
    Box,
    Button,
    CloseButton,
    Divider,
    HStack,
    Input,
    Spacer,
    Tag, TagCloseButton, TagLabel,
    Text,
    VStack,
    Wrap,
    WrapItem
} from "@chakra-ui/react";
import {AiOutlineUsergroupAdd, MdPublic} from "react-icons/all";
import {CopyIcon} from "@chakra-ui/icons";
import XLSX from 'xlsx';

function MdOutlinePublic() {
    return null;
}

const ShareDialog = ({setVisible}) => {

    const [emails, setEmails] = useState([]);
    const [email, setEmail] = useState('');

    const importExcel = (e) => {
        const file = e.target.files[0];
        const reader = new FileReader();
        reader.onload = (event) => {
            const bstr = event.target.result;
            const workBook = XLSX.read(bstr, {type: 'binary'});
            //get first sheet
            const workSheetName = workBook.SheetNames[0];
            const workSheet = workBook.Sheets[workSheetName];
            const fileData = XLSX.utils.sheet_to_json(workSheet, {header: 1});
            console.log(fileData);

        }
        reader.readAsBinaryString(file);
    }
    return (
        <>
            <Box bg={'gray.400'}
                 opacity={.8}
                 position={'fixed'}
                 top={0} minH={'100vh'}
                 right={0}
                 onClick={() => setVisible(false)}
                 minW={'100vw'}
                 zIndex={900}>

            </Box>
            <Box p={5} w={500} bg={'white'}
                 borderRadius={'md'}
                 boxShadow={'md'}
                 transform={"translate(50%,-50%)"}
                 position={'fixed'} top={'40%'}
                 right={'50%'}
                 height={'auto'} zIndex={901}>
                <VStack spacing={5} w={'100%'}>
                    <HStack w={'100%'}>
                        <AiOutlineUsergroupAdd size={'20'}/>
                        <Text fontSize={'xl'} fontWeight={'medium'}>Invite</Text>
                        <Spacer/>
                        <CloseButton
                            bg={'red'}
                            onClick={() => setVisible(false)}
                            size={'md'}/>
                    </HStack>

                    <VStack w={'100%'} align={'start'}>
                        <Text fontWeight={'medium'}>Share public link</Text>
                        <HStack w={'100%'}>
                            <MdPublic size={25}/>
                            <Input
                                size={'sm'}
                            />
                            <CopyIcon size={30}/>
                        </HStack>
                    </VStack>

                    <VStack w={'100%'} align={'start'}>
                        <Text fontWeight={'medium'}>Share to student (using email)</Text>
                        <Wrap w={'100%'}>
                            {
                                emails.map((email) => (
                                    <WrapItem key={email}>
                                        <Tag
                                            size={'sm'}
                                            borderRadius="full"
                                            variant="solid"
                                            colorScheme="green"
                                        >
                                            <TagLabel>{email}</TagLabel>
                                            <TagCloseButton onClick={() => {
                                                let filteredEmail = emails.filter(e => e != email)
                                                setEmails(filteredEmail);
                                            }}/>
                                        </Tag>
                                    </WrapItem>
                                ))
                            }
                        </Wrap>
                        <HStack w={'100%'}>
                            <Input
                                size={'sm'}

                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                placeHolder={'Email to share. Ex: test@gmail.com'}/>
                            <Button
                                onClick={() => {
                                    setEmail('')
                                    if (!emails.includes(email)) {
                                        setEmails(prevState => [...prevState, email])
                                    }
                                }}
                                isDisabled={email.length === 0}
                                size={'sm'} colorScheme={'teal'}>Add</Button>
                        </HStack>
                        {/*<HStack w={'100%'}>*/}
                        {/*    <Input*/}
                        {/*        onChange={importExcel}*/}
                        {/*        type={'file'}*/}
                        {/*    />*/}
                        {/*</HStack>*/}
                       <HStack w={'100%'} py={5} justifyContent={'end'} >
                           <Button colorScheme={'teal'}>
                               Share invite
                           </Button>
                       </HStack>
                    </VStack>
                </VStack>
            </Box>
        </>
    );
};

export default ShareDialog;
