import React from 'react';
import {
    Box,
    Button,
    Center,
    Flex,
    Grid,
    GridItem,
    Heading,
    HStack, IconButton,
    Image, Input,
    SimpleGrid,
    Text,
    VStack
} from "@chakra-ui/react";
import {AddIcon, ArrowBackIcon, EditIcon} from "@chakra-ui/icons";

const CreateQuestionPage = () => {
    return (
        <Box
            align={'center'}
            bg={'#F7FAFC'}
            minH={'100vh'}>
            {/*Header*/}
            <Box minH={'50px'} maxH={'80px'} bg={'teal.600'} w={'100%'}>
                <Flex alignItems={'center'} justifyContent={'space-between'} p={4}>
                    <ArrowBackIcon fontSize={35}/>
                    <Text>Editted 1 minute ago</Text>
                    <Button>Publish</Button>
                </Flex>
            </Box>
            {/*    Content*/}
            <Flex alignItems={'center'} justifyContent={'center'}>
                <Box flex={'7'}>
                    <Flex direction={'column'} justifyContent={'center'} alignItems={'center'}>
                        <Box align={'center'}
                             bg={'white'}
                            // maxW={'600px'}
                             boxShadow={'md'}
                             borderBottomRadius={'3xl'}
                            // p={10}
                             p={8}
                        >
                            <HStack spacing={4}>
                                <VStack w={'400px'}

                                >
                                    <Text alignSelf={'start'}>Search</Text>
                                    <Input isFullWidth={true}/>
                                </VStack>
                                <VStack align={'end'} w={'200px'}>
                                    <Text>Create question</Text>
                                    <Button size={'md'} rightIcon={<AddIcon/>} variant={'solid'} colorScheme={'teal'}>Add
                                        question</Button>
                                </VStack>
                            </HStack>
                        </Box>
                        <Box minH={'20px'}/>

                        <SimpleGrid
                            p={'10'}
                            bg={'white'}
                            align={'center'}
                            justifyContent={'center'}
                            justifyItems={'center'}
                            minChildWidth="100px"
                            maxW={'500px'}
                            spacing={2}>
                            {
                                [1, 2, 2, 2, 2, 2].map((item, index) => (
                                    <VStack cursor={'pointer'} key={index}>
                                        <Image
                                            boxSize="80px"
                                            objectFit="cover"
                                            src="https://cdn-icons-png.flaticon.com/512/1168/1168671.png"
                                            alt="Segun Adebayo"
                                        />
                                        <Text
                                            fontSize={15}
                                            color={'gray.600'}
                                            fontWeight={'medium'}
                                        >Checkbox</Text>
                                    </VStack>
                                ))
                            }
                        </SimpleGrid>

                    </Flex>
                </Box>
                <Box minH={'70vh'} flex={'3'} justifyItems={'start'} justifyContent={'start'}>
                    <Box bg={'red'} maxW={'200px'} w={'100%'}>
                        <VStack spacing={5}>
                            <Input type={'file'} isFullWidth={true}/>

                            <IconButton icon={<EditIcon/>}/>
                        </VStack>
                    </Box>
                </Box>
            </Flex>


        </Box>
    );
};

export default CreateQuestionPage;
