import React, {useContext, useEffect, useState} from 'react';
import {Box, Button, Divider, Flex, Heading, HStack, Text} from "@chakra-ui/react";
import UserService from "../../../service/UserService";
import {useParams} from "react-router-dom";
import {AdminContext} from "../../../providers/AdminSettingProvider";
import {formatDateDisplay, isOutDate} from "../../../utils/ApplicationUtils";

const AssignedInfo = () => {
    const {isLoading, setLoading} = useContext(AdminContext);
    const {id} = useParams();

    const [info, setInfo] = useState({});
    useEffect(() => {
        getInfo();
    }, []);

    const getInfo = async () => {
        try {
            setLoading(true)
            const resp = await UserService.getAssignedQuizInfo(id);
            console.log(resp.data);
            setInfo(resp.data);
        } catch (e) {

        } finally {
            setLoading(false)
        }
    }
    return (
        <Flex>
            <Flex bg={'white'} p={'5'} direction={'column'} flex={7}>
                <Heading>TITLE</Heading>
                <HStack>
                    <Text>{info && info.title}</Text>
                    <Text>{info && info.createDate}</Text>
                </HStack>
                <Text>{info && info.description}</Text>
                <Divider py={'2'}/>
                <Flex py={2} justifyContent={'end'}>
                    <Text>Den han</Text>
                    <Box w={'20px'}/>
                    <Text>{formatDateDisplay(info && info.finishDate)}</Text>
                </Flex>
            </Flex>
            <Box w={'20px'}></Box>
            {!isLoading && info &&
            <Flex bg={'white'} p={'5'} direction={'column'} justifyContent={'space-between'} flex={3}>
                <Flex justifyContent={"space-between"}>
                    <Text>Status</Text>
                    <Text
                        fontWeight={'medium'}>{info.status === 1 ? 'Da nop'
                        : (info.status === 0 && isOutDate(info.finishDate) ? 'Thieu' : 'Da giao')}</Text>
                </Flex>
                <Box w={'100%'}>
                    <Button
                        disabled={info.status == 1 || isOutDate(info.finishDate)}
                        colorScheme={'blue'} w={'100%'}>Start</Button>
                </Box>
            </Flex>}
        </Flex>
    );
};

export default AssignedInfo;
