import React from 'react';
import {Box, Button, Center, Flex, Grid, GridItem, Heading, Image, SimpleGrid, Text} from "@chakra-ui/react";
import {ArrowBackIcon} from "@chakra-ui/icons";

const CreateQuestionPage = () => {
    return (
        <Box
            align={'center'}
            bg={'#F7FAFC'}
            minH={'100vh'}>
            {/*Header*/}
            <Box minH={'60px'} maxH={'80px'} bg={'teal.600'} w={'100%'}>
                <Flex alignItems={'center'} justifyContent={'space-between'} p={4}>
                    <ArrowBackIcon fontSize={35}/>
                    <Text>Editted 1 minute ago</Text>
                    <Button>Publish</Button>
                </Flex>
            </Box>
            {/*    Content*/}
         <Flex>
             <Center flex={'7'}  height={'80vh'}>
                 <SimpleGrid  bg={'red'} minChildWidth="120px" maxW={'500px'} spacing={5}>
                     <Box>
                         <Image
                             boxSize="100px"
                             objectFit="cover"
                             src="https://cdn-icons-png.flaticon.com/512/1168/1168671.png"
                             alt="Segun Adebayo"
                         />
                     </Box>

                 </SimpleGrid>
             </Center>
             <Box  flex={3}>
                 fsdsd
             </Box>
         </Flex>


        </Box>
    );
};

export default CreateQuestionPage;
