import React from 'react';
import {Box, Center, Checkbox, Container, HStack, Input, Textarea, VStack} from "@chakra-ui/react";

const QuestionDialog = () => {
    return (
        <>
            <Box
                onClick={() => {
                    console.log("HAHHAHA")
                }}
                position={'fixed'}
                zIndex={9000}
                top={0}
                bg={'gray.300'}
                align={'center'}
                alignItems={'center'}
                opacity={.4}
                justifyContent={'center'}
                width={'100vw'}
                height={'100vh'}>
            </Box>

            <Box
                position={'fixed'}
                zIndex={9001}
                top={'50%'}
                right={'50%'}
                transform={
                    'translate(50%,-50%)'
                }
                minW={'500px'}
                bg={'white'}
                maxW={'500px'}
                minH={'500px'}
                // top={10} right={50}
            >
                <VStack p={2}>
                    <Textarea placeholder={'Type your question here'}/>
                    <HStack w={'100%'}>
                        <Checkbox/>
                        <Container maxW="xl" centerContent>
                            <Box padding="4" bg="gray.100" maxW="3xl">
                                <Input isFullWidth={true} size={'sm'}/>
                            </Box>
                        </Container>
                    </HStack>
                </VStack>
            </Box>
        </>

    );
};

export default QuestionDialog;
