import React from 'react';
import {
    Box,
    Button,
    Center,
    Flex,
    Grid,
    GridItem,
    SimpleGrid,
    Input,
    Stack,
    Wrap,
    WrapItem,
    Heading
} from "@chakra-ui/react";
import Card from "./card";
import Footer from "../footer";

const HomeScreen = () => {
    return (
        <Flex  direction={'column'} >
            {/*//Main Content*/}
            <Flex
                p={8}
                bg={'#F7FAFC'}
                minHeight={'100vh'}
                w={'100%'}
                direction={'column'}
                alignItems={'flex-start'}
            >
                <Flex w={'100%'} bg={'yellow'} justifyContent={'space-around'}>
                    <Box
                        // minW={'800px'}
                        w={['100px','500px','800px']}
                    >
                        <Stack boxShadow={'md'}
                               borderRadius={'10px'}
                               bg={'white'}
                               p={['10px','50px','100px']}
                               minW={'360px'}
                               direction={'row'}

                        >
                            <Input minH={'50px'} minW={'260px'} placeholder={'Enter a join code'}/>
                            <Button minH={'50px'} minW={'30px'}
                                    bgGradient="linear(to-l, #7928CA, #FF0080)"
                                    color={'white'}>Join</Button>
                        </Stack>
                    </Box>
                    <Box  bg={'red'} p={'100px'} align={'center'} >
                        <Stack direction={'column'}>
                            <Text>Nguyen Duy</Text>

                        </Stack>
                    </Box>
                </Flex>


            </Flex>
            <Footer/>

            {/*    end of main content*/}
        </Flex>
    );
};

export default HomeScreen;
