import React, {useState} from 'react';
import {Box, Heading, Input, ModalOverlay, Textarea, VStack} from "@chakra-ui/react";

const NewQuestionDialog = ({visible, setVisible}) => {

    const option = {
        content: "option contnt"
    }
    const [question, setQuestion] = useState(null);
    const [options, setOptions] = useState([option, option]);

    const onOptionChange = (e, index) => {
        //push new option if it is the last option
        if (index === options.length - 1) {
            options.push({});
        }
        setOptions((prevState) => (
            prevState.map((option, i) => i === index
                ? {
                    ...option,
                    content: e.target.value
                }
                : option)
        ));

    }
    return (
        <>
            <Box
                onClick={() => setVisible(false)}
                zIndex={1000}
                bg={'gray.600'}
                opacity={.5}
                position={'fixed'}
                right={0}
                top={0}
                minW={'100vw'}
                minH={'100vh'}/>
            <Box zIndex={1001}
                 position={'fixed'}
                 right={'50%'}
                 top={'40%'}
                 bg={'gray.100'}
                 transform={"translate(50%,-50%)"}
            >
                <VStack minW={800} p={5}>
                    <Heading>Add questionss</Heading>
                    <Box w={'100%'}>
                        <Textarea borderColor={'black'} placeholder="Here is a sample placeholder"/>
                    </Box>
                    <VStack w={'100%'}>
                        {
                            options.map((option, index) => (
                                <Input
                                    onChange={(e) => onOptionChange(e, index)}
                                    key={index}
                                    isFullWidth={true} placeholder={"option"}/>
                            ))
                        }
                    </VStack>
                </VStack>
            </Box>
        </>
    );
};

export default NewQuestionDialog;
