import React from 'react';
import {
    Box,
    Button,
    Center, Checkbox, Container,
    Flex, Heading,
    HStack,
    IconButton,
    Image,
    Input,
    SimpleGrid,
    Spacer, Stack,
    Text, Tooltip,
    VStack
} from "@chakra-ui/react";
import {AddIcon, ArrowBackIcon, CopyIcon, DeleteIcon, EditIcon, TimeIcon, ViewIcon} from "@chakra-ui/icons";

const QuizCreatePage = () => {
    return (
        <Box bg={'#F7FAFC'}>
            {/*  Header with action bar*/}
            <Box zIndex={1000} bg={'red'} position={'fixed'} top={0} w={'100%'} bg={'#F7FAFC'}>
                {/*Header*/}
                <Box bg={'red'} maxH={'60px'} bg={'teal.600'}>
                    <Flex align={'center'} p={2} alignItems={'center'} justifyContent={'space-between'}>
                        <ArrowBackIcon fontSize={35}/>
                        <Text>Editted 1 minute ago</Text>
                        <Button>Publish</Button>
                    </Flex>
                </Box>
                {/*Box*/}
                <Box bg={'#F7FAFC'} maxW={'860px'}>
                    <Box>
                        <Center>
                            <HStack boxShadow={'md'} p={4} borderBottomRadius={'xl'} spacing={4} bg={'white'}>
                                <VStack w={'400px'}>
                                    <Text alignSelf={'start'}>Search</Text>
                                    <Input isFullWidth={true}/>
                                </VStack>
                                <VStack align={'end'} w={'200px'}>
                                    <Text>Create question</Text>
                                    <Button size={'md'} rightIcon={<AddIcon/>} variant={'solid'} colorScheme={'teal'}>Add
                                        question</Button>
                                </VStack>
                            </HStack>
                        </Center>
                    </Box>
                </Box>

            </Box>
            {/*   Content with right sidebar*/}
            <Box pt={'150px'} minH={'100vh'} bg={'#F7FAFC'} position={'relative'}>
                <Flex direction={'row'}>
                    {/*Question*/}
                    <Box flex={7} mt={10} w={'100%'} maxW={'860px'}>
                        <Center>
                            {/*<SimpleGrid*/}
                            {/*    boxShadow={'md'}*/}
                            {/*    h={'100%'}*/}
                            {/*    p={10}*/}
                            {/*    bg={'white'}*/}
                            {/*    align={'center'}*/}
                            {/*    justifyContent={'center'}*/}
                            {/*    justifyItems={'center'}*/}
                            {/*    minChildWidth="100px"*/}
                            {/*    maxW={'500px'}*/}
                            {/*    spacing={2}>*/}
                            {/*    {*/}
                            {/*        [1, 2, 2, 2, 2, 2].map((item, index) => (*/}
                            {/*            <VStack cursor={'pointer'} key={index}>*/}
                            {/*                <Image*/}
                            {/*                    boxSize="80px"*/}
                            {/*                    objectFit="cover"*/}
                            {/*                    src="https://cdn-icons-png.flaticon.com/512/1168/1168671.png"*/}
                            {/*                    alt="Segun Adebayo"*/}
                            {/*                />*/}
                            {/*                <Text*/}
                            {/*                    fontSize={15}*/}
                            {/*                    color={'gray.600'}*/}
                            {/*                    fontWeight={'medium'}*/}
                            {/*                >Checkbox</Text>*/}
                            {/*            </VStack>*/}
                            {/*        ))*/}
                            {/*    }*/}
                            {/*</SimpleGrid>*/}
                            <Stack w={'100%'} direction={'column'} align={'center'}>
                                {
                                    [1, 2, 2, 2, 2, 2,].map((item, i) => (
                                        <Box key={i} my={5} borderRadius={'md'} align={'start'} textAlign={'start'}
                                             h={'100%'} bg={'teal'} maxW={'600px'}
                                             minW={'600px'}>
                                            {/*Edit bar*/}
                                            <HStack p={2} bg={'gray.200'}>
                                                <Text>Question1</Text>
                                                <Spacer/>
                                                <Button>Edit</Button>
                                                <IconButton icon={<DeleteIcon/>}/>
                                                <IconButton icon={<CopyIcon/>}/>
                                            </HStack>
                                            <VStack align={'start'} p={2} spacing={5}>
                                                {/*    Question title*/}
                                                <Text>This is question 1</Text>
                                                <SimpleGrid columns={2} spacing={2}>
                                                    {
                                                        [1, 2, 3, 4].map((item, index) => (
                                                            // <Container key={index} maxW="container.lg">
                                                            //     <Checkbox size={'md'}>
                                                            //         This is question1asdfffffffffffffffffffffffffffffffffff
                                                            //     </Checkbox>
                                                            // </Container>

                                                            <Container maxW="xl" centerContent>
                                                                <Box padding="1" maxW="3xl">
                                                                    <Checkbox size={'sm'}>
                                                                        There are many benefits to a joint design and
                                                                        development system. Not only
                                                                        does it bring benefits to the design team.
                                                                    </Checkbox>

                                                                </Box>
                                                            </Container>
                                                        ))
                                                    }


                                                </SimpleGrid>
                                            </VStack>
                                        </Box>
                                    ))
                                }
                            </Stack>
                        </Center>
                    </Box>
                    {/**/}
                    <Box
                        overflowY={'scroll'}
                        top={'150'}
                        zIndex={1000000}
                        position={'sticky'}
                        bg={'white'}
                        boxShadow={'md'}
                        minW={'300px'}
                        maxW={'300px'}
                        minH={'400px'}
                        height={'100%'}
                        mb={100}
                        right={'50px'}>
                        <Box p={4} mb={100}>
                            <VStack spacing={5}>
                                <Input type={'file'}/>
                                <HStack>
                                    <Tooltip label={'Quiz title'}>
                                        <Heading fontWeight={'medium'} fontSize={'25px'}>This is quiz title</Heading>
                                    </Tooltip>
                                    <IconButton
                                        colorScheme={'teal'}
                                        onClick={() => {
                                        }}
                                        icon={<EditIcon/>}/>
                                </HStack>
                                <HStack>
                                    <Tooltip
                                        label={'Public: visible to everyone ;\n Private: visible to you and shared ones'}>
                                        <Button size={'sm'} leftIcon={<ViewIcon/>} variant={'solid'}
                                                colorScheme={'white'} color={'blue.400'}>public</Button>
                                    </Tooltip>
                                    <Tooltip label={'Default time for all questions'}>
                                        <Button size={'sm'} leftIcon={<TimeIcon/>} variant={'solid'}
                                                colorScheme={'white'} color={'blue.400'}>30 secs</Button>
                                    </Tooltip>
                                </HStack>

                            </VStack>
                        </Box>
                    </Box>
                </Flex>

            </Box>
        </Box>
    );
};

export default QuizCreatePage;
