import React, {useState, useContext, createContext, useEffect} from "react";
import Axios from "axios";
import axios from "axios";

// Provider hook that creates auth object and handle state
export const AuthProvider = ({children}) => {

    const [user, setUser] = useState(null);
    const [isAuthenticated, setAuthenticated] = useState(false);

    const [isLoading, setLoading] = useState(true);
    //login
    const login = async (username, password, remember) => {
        try {
            let resp = await Axios.post("http://localhost:8080/api/v1/authenticate/login", {
                "email": username,
                "password": password
            });
            if (resp.status === 200) {
                let user = resp.data.user;
                if (remember) {
                    localStorage.setItem("accessToken", resp.data.access_token);
                    localStorage.setItem("refreshToken", resp.data.refresh_token);
                }
                localStorage.setItem("user", JSON.stringify(user));
                setUser(JSON.parse(localStorage.getItem('user')));
                return true;
            }
            return false;
        } catch (e) {
            return false;
        }
    };

    //logout
    const logout = () => {
        setUser(
            null
        );
        localStorage.removeItem('user')
        localStorage.removeItem('accessToken')
        localStorage.removeItem('refreshToken')
    }

    //create value
    const value = {
        user,
        setUser,
        login,
        logout,
        isAuthenticated,
        setAuthenticated,
    };
    useEffect(() => {
        setLoading(true);
        const accessToken = localStorage.getItem("accessToken");
        if (accessToken != null) {
            setLoading(true);
            axios.get("http://localhost:8080/api/v1/member/users/user-details", {
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${accessToken}`
                }
            })
                .then(resp => {
                    if (resp.status === 200) {
                        setUser(resp.data);
                        console.log(resp.data);
                        localStorage.setItem("user", JSON.stringify(resp.data));
                        return;
                    } else if (resp.status === 401) {
                        //refresh token
                    } else {
                        logout();
                    }
                }).catch(
                () => {
                    logout();
                    setLoading(false);
                }
            ).finally(() => {
                setLoading(false);
            })

        } else {
            setLoading(false)
        }
    }, []);
    return (
        <>
            {
                !isLoading && <AuthContext.Provider value={value}>{children}</AuthContext.Provider>
            }
        </>
    )
        ;
};
//create auth context
const AuthContext = createContext();

export const useAuth = () => {
    return useContext(AuthContext);
};
