import React from 'react';
import {
    Box,
    Button,
    Checkbox,
    Container,
    Divider, Flex,
    Heading,
    HStack, IconButton,
    SimpleGrid,
    Text,
    Tooltip,
    VStack
} from "@chakra-ui/react";
import {ArrowDownIcon, ArrowUpIcon, TimeIcon} from "@chakra-ui/icons";
import {useQuiz} from "../../../context/QuizProvider";

const StartQuiz = () => {
    const {foundedQuiz,questions} = useQuiz();

    return (
        <>
            <Box
                m={10}
                bg={'white'}
                borderRadius={'md'}
                boxShadow={'xl'}
                minH={'100vh'}
                maxW={'860px'}
                p={10}>
                <VStack position={'relative'}>
                    {/*
                           QUIZ TITLE
                           */}
                    <Heading
                        pb={10}
                        fontSize={25}>
                        {foundedQuiz.title != null ? foundedQuiz.title : ''}
                    </Heading>
                    <Text
                        fontWeight={'medium'}
                        color={'teal'}
                        letterSpacing={1}
                        fontStyle={'italic'}
                        position={'absolute'}
                        top={0}
                        right={0}
                        m={5}>
                        {questions.length} Questions
                    </Text>
                    {/*QUESTIONS*/}
                    {
                        questions.map((question, i) => (
                            <VStack
                                w={'100%'}
                                align={'start'}
                                textAlign={'start'
                                } key={i}
                                p={2}
                                spacing={5}>
                                {/*
                                    QUESTION TITLE
                                    */}
                                <Container w={'100%'} maxW="4xl">
                                    <Box maxW="4xl">
                                        <Text fontWeight={'medium'}>
                                            {i + 1}. {question.title} \t QuestionID : {question.questionId}
                                        </Text>
                                    </Box>
                                </Container>
                                {/*OPTIONS*/}
                                <SimpleGrid
                                    w={'100%'}
                                    columns={2}
                                    spacing={2}
                                    align={'start'}
                                    textAlign={'start'}>
                                    {
                                        question.options.map((option, i) => (
                                            <Container
                                                w={'100%'}
                                                minW={300}
                                                key={i}
                                                maxW="xl"
                                                align={'start'}
                                                textAlign={'start'}
                                                alignItems={'start'}
                                            >
                                                <Box padding="1" maxW="3xl">
                                                    <Checkbox
                                                        // onChange={(e) => onSelectedOption(e, option.optionId, question.questionId)}
                                                        value={option.optionId}
                                                        colorScheme={'teal'}
                                                        size={'md'}>
                                                        {option.content} \t OptionID : {option.optionId}
                                                    </Checkbox>
                                                </Box>
                                            </Container>
                                        ))
                                    }
                                </SimpleGrid>
                                <Divider/>
                            </VStack>
                        ))
                    }
                    {/* Submit button*/}
                    <Box py={5} align={'end'} alignSelf={'end'}>
                        <Button alignSelf={'end'} size={'lg'} colorScheme={'teal'}>Submit</Button>
                    </Box>
                </VStack>

                {/* FIXED CONTENTS*/}
                <HStack zIndex={100} position={'fixed'} bottom={10} right={20}>
                    <Tooltip label={"Scroll to top"}>
                        <IconButton
                            onClick={() => window.scrollTo(0, 0)}
                            colorScheme={'teal'}
                            icon={<ArrowUpIcon/>}/>
                    </Tooltip>
                    <Tooltip label={"Scroll to bottom"}>
                        <IconButton
                            // aria-label={""}
                            onClick={() => {
                                // pageRef.current.scrollIntoView({
                                //     behavior: "smooth",
                                //     block: "end",
                                //     inline: "nearest"
                                // })
                            }}
                            colorScheme={'teal'}
                            icon={<ArrowDownIcon/>}/>
                    </Tooltip>
                </HStack>
                <Flex p={1} position={'fixed'} left={0} top={0} m={10}>
                    <Button size={'md'} colorScheme={'teal'} variant={'outline'} leftIcon={<TimeIcon/>}>23:00
                        left</Button>
                </Flex>
            </Box>
        </>
    );
};

export default StartQuiz;
