import React, {useRef, useState} from 'react';
import {
    Box,
    Button,
    Center, Checkbox, Container,
    Divider,
    Flex, FormControl, FormLabel,
    GridItem,
    Heading,
    HStack, IconButton,
    Image, Progress,
    SimpleGrid, Switch,
    Text, Tooltip,
    VStack
} from "@chakra-ui/react";
import {useParams} from "react-router-dom";
import {BiShare, BsSkipStart, FcStart} from "react-icons/all";
import SettingDialog from "./SettingDialog";
import {ArrowDownIcon, ArrowUpIcon, TimeIcon} from "@chakra-ui/icons";
import {useQuiz} from "../../context/QuizProvider";

const GradeQuiz = () => {

    //get id in params
    const {id} = useParams();
    const pageRef = useRef(null);
    const {foundedQuiz} = useQuiz();
    const [showSetting, setShowSetting] = useState(false);

    return (
        <div ref={pageRef}>
            <Box minH={'100vh'} p={5} bg={'gray.100'} align={'center'}>
                {/*<Box minH={'80px'}></Box>*/}
                {showSetting && <SettingDialog setShow={setShowSetting}/>}
                <Box bg={'white'}
                     borderRadius={'md'}
                     boxShadow={'xl'}
                     minH={'100vh'}
                     maxW={'860px'}
                     p={5}>
                    <VStack position={'relative'}>
                        {/*
                        QUIZ TITLE
                        */}
                        <Heading fontSize={25}>{foundedQuiz.title != null ? foundedQuiz.title : ''}</Heading>
                        <Text position={'absolute'} top={0} right={0} m={5}>
                            {foundedQuiz.questions.length} Questions
                        </Text>
                        {/*QUESTIONS*/}
                        {
                            foundedQuiz.questions.map((q, i) => (
                                <VStack bg={'yellow'} w={'100%'} align={'start'} textAlign={'start'} key={i} p={2} spacing={5}>
                                    {/*
                                    QUESTION TITLE
                                    */}
                                    <Container  maxW="3xl">
                                        <Box maxW="3xl">
                                            <Text fontWeight={'medium'}>
                                                {i + 1}. {q.title}
                                            </Text>
                                        </Box>
                                    </Container>
                                    {/*OPTIONS*/}
                                    <SimpleGrid columns={2} spacing={2} bg={'red'} align={'start'} textAlign={'start'}>
                                        {
                                            q.options.map((option, i) => (
                                                <Container
                                                    minW={200}
                                                    key={i}
                                                    maxW="xl"
                                                    bg={'blue'}
                                                    align={'start'}
                                                    textAlign={'start'}
                                                    alignItems={'start'}
                                                    justifyContent={'start'}
                                                    // justifyItems={'start'}
                                                    >
                                                    <Box  maxW="3xl">
                                                        <Checkbox size={'sm'}>
                                                            {/*option.*/}
                                                            {option.content}
                                                        </Checkbox>
                                                    </Box>
                                                </Container>
                                            ))
                                        }
                                    </SimpleGrid>
                                    <Divider/>
                                </VStack>
                            ))
                        }
                        {/* Submit button*/}
                        <Box py={5} align={'end'} alignSelf={'end'}>
                            <Button alignSelf={'end'} size={'lg'} colorScheme={'teal'}>Submit</Button>
                        </Box>
                    </VStack>
                </Box>

                {/*Fixed action buttons*/}
                <HStack zIndex={100} position={'fixed'} bottom={0} right={0} m={10}>
                    <Tooltip label={"Scroll to top"}>
                        <IconButton
                            onClick={() => window.scrollTo(0, 0)}
                            colorScheme={'teal'}
                            icon={<ArrowUpIcon/>}/>
                    </Tooltip>
                    <Tooltip label={"Scroll to bottom"}>
                        <IconButton
                            onClick={() => {
                                pageRef.current.scrollIntoView({behavior: "smooth", block: "end", inline: "nearest"})
                            }}
                            colorScheme={'teal'}
                            icon={<ArrowDownIcon/>}/>
                    </Tooltip>
                </HStack>


                <Flex p={1} position={'fixed'} left={0} top={0} m={10}>
                    <Button size={'md'} colorScheme={'teal'} variant={'outline'} leftIcon={<TimeIcon/>}>23:00
                        left</Button>
                </Flex>


            </Box>
            <Progress
                size={'sm'}
                left={'50%'}
                transform={'translateX(-50%)'}
                minW={'860px'}
                maxW={'860px'}
                position={'fixed'} bottom={0} value={64}/>
        </div>
    );
};

export default GradeQuiz;
