import React, {useEffect, useRef, useState} from 'react';
import {
    Box,
    Button,
    Checkbox,
    Container,
    Divider, Flex,
    Heading,
    HStack, IconButton,
    SimpleGrid,
    Text,
    Tooltip,
    VStack,
    AlertDialog,
    AlertDialogBody,
    AlertDialogFooter,
    AlertDialogHeader,
    AlertDialogContent,
    AlertDialogOverlay,
} from "@chakra-ui/react";
import {ArrowDownIcon, ArrowUpIcon, TimeIcon} from "@chakra-ui/icons";
import {useQuiz} from "../../../context/QuizProvider";
import axios from "axios";
import {useHistory, useParams} from "react-router-dom";
import quizApi from "../../../api/quizApi";
import CustomAlertDialog from "../../../components/CustomAlertDialog";

const StartQuiz = () => {

    const {foundedQuiz, setFoundedQuiz} = useQuiz();
    const {code} = useParams();
    const [isLoading, setLoading] = useState(true);
    const [isOpen, setOpen] = useState(false);
    const cancelRef = useRef();
    const [submitContent, setSubmitContent] = useState({});
    const history = useHistory();

    useEffect(() => {
        console.log('start quiz')
        const getQuizContent = async () => {
            setLoading(true);
            try {
                const data = await quizApi.getQuizContent(code);
                //
                if (data.status === 0 || data.visibility === 0) {
                    return history.replace('/');
                }
                if (data.visibility === 1) {
                    try {
                        await quizApi.getAssignedQuiz(data.quizId);
                        setFoundedQuiz(data);
                        setLoading(false);
                    } catch (e) {
                        return history.replace(`/`);
                    }
                }
                console.log(data);
            } catch (e) {
                setLoading(false);
                console.log('Failed to get quiz details');
            }
        }
        getQuizContent();
    }, []);


    return (
        <>
            <CustomAlertDialog
                title={'Submit answers'}
                description={'Are you sure? You can\'t undo this action afterwards.'}
                isOpen={isOpen}
                setOpen={setOpen}
                cancelRef={cancelRef}
                onSubmit={() => {
                    console.log('on submit')
                }}
            />
            {/*<AlertDialog*/}
            {/*    isOpen={isOpen}*/}
            {/*    leastDestructiveRef={cancelRef}*/}
            {/*    onClose={() => setOpen(false)}*/}
            {/*>*/}
            {/*    <AlertDialogOverlay>*/}
            {/*        <AlertDialogContent>*/}
            {/*            <AlertDialogHeader fontSize="lg" fontWeight="bold">*/}
            {/*                Submit answers*/}
            {/*            </AlertDialogHeader>*/}

            {/*            <AlertDialogBody>*/}
            {/*                Are you sure? You can't undo this action afterwards.*/}
            {/*            </AlertDialogBody>*/}

            {/*            <AlertDialogFooter>*/}
            {/*                <Button ref={cancelRef} onClick={() => setOpen(false)}>*/}
            {/*                    Cancel*/}
            {/*                </Button>*/}
            {/*                <Button colorScheme="teal" onClick={() => setOpen(false)} ml={3}>*/}
            {/*                    Delete*/}
            {/*                </Button>*/}
            {/*            </AlertDialogFooter>*/}
            {/*        </AlertDialogContent>*/}
            {/*    </AlertDialogOverlay>*/}
            {/*</AlertDialog>*/}
            <Box minH={'100vh'} bg={'blue.100'} p={10}
                 align={'center'}>
                {
                    !isLoading && <Box
                        bg={'white'}
                        borderRadius={'md'}
                        boxShadow={'xl'}
                        minH={'100vh'}
                        maxW={'860px'}
                        p={10}>
                        <VStack position={'relative'}>
                            {/*
                           QUIZ TITLE
                           */}
                            <Heading
                                pb={10}
                                color={'purple.600'}
                                fontSize={30}>
                                {foundedQuiz != null ? foundedQuiz.title : ''}
                            </Heading>
                            {/*NUMBER OF QUESTIONS*/}
                            <Text
                                fontWeight={'medium'}
                                color={'teal'}
                                letterSpacing={1}
                                fontStyle={'italic'}
                                position={'absolute'}
                                top={0}
                                right={0}
                                m={5}>
                                {foundedQuiz.questions && foundedQuiz.questions.length} Questions
                            </Text>
                            {/*QUESTIONS*/}
                            {
                                foundedQuiz.questions.map((question, i) => (
                                    <VStack
                                        w={'100%'}
                                        align={'start'}
                                        textAlign={'start'} key={i}
                                        p={2}
                                        spacing={5}>
                                        {/*
                                    QUESTION TITLE
                                    */}
                                        <Container w={'100%'} maxW="4xl">
                                            <Box maxW="4xl">
                                                <Text fontWeight={'medium'} fontSize={20} color={'purple.900'}>
                                                    {i + 1}. {question.title} \t QuestionID : {question.questionId}
                                                </Text>
                                            </Box>
                                        </Container>
                                        {/*OPTIONS*/}
                                        <SimpleGrid
                                            w={'100%'}
                                            columns={2}
                                            spacing={4}
                                            align={'start'}
                                            textAlign={'start'}>
                                            {
                                                question.options.map((option, i) => (
                                                    <Container
                                                        w={'100%'}
                                                        minW={300}
                                                        key={i}
                                                        // maxW="xl"
                                                        align={'start'}
                                                        textAlign={'start'}
                                                        alignItems={'start'}
                                                    >
                                                        <Box
                                                            padding={1}
                                                            // maxW="3xl"
                                                        >
                                                            <Checkbox
                                                                // onChange={(e) => onSelectedOption(e, option.optionId, question.questionId)}
                                                                value={option.optionId}
                                                                colorScheme={'teal'}
                                                                size={'lg'}>
                                                                {option.content} \t OptionID : {option.optionId}
                                                            </Checkbox>
                                                        </Box>
                                                    </Container>
                                                ))
                                            }
                                        </SimpleGrid>
                                        <Divider/>
                                    </VStack>
                                ))
                            }
                            {/* Submit button*/}
                            <Box py={5} align={'end'} alignSelf={'end'}>
                                <Button
                                    onClick={() => setOpen(true)}
                                    alignSelf={'end'} size={'lg'} colorScheme={'teal'}>Submit</Button>
                            </Box>
                        </VStack>

                        {/* FIXED CONTENTS*/}
                        <HStack zIndex={100} position={'fixed'} bottom={10} right={20}>
                            <Tooltip label={"Scroll to top"}>
                                <IconButton
                                    onClick={() => window.scrollTo(0, 0)}
                                    colorScheme={'teal'}
                                    icon={<ArrowUpIcon/>}/>
                            </Tooltip>
                            <Tooltip label={"Scroll to bottom"}>
                                <IconButton
                                    // aria-label={""}
                                    onClick={() => {
                                        // pageRef.current.scrollIntoView({
                                        //     behavior: "smooth",
                                        //     block: "end",
                                        //     inline: "nearest"
                                        // })
                                    }}
                                    colorScheme={'teal'}
                                    icon={<ArrowDownIcon/>}/>
                            </Tooltip>
                        </HStack>
                        <Flex p={1} position={'fixed'} left={0} top={0} m={10}>
                            <Button size={'md'} colorScheme={'teal'} variant={'outline'} leftIcon={<TimeIcon/>}>23:00
                                left</Button>
                        </Flex>
                    </Box>


                }
            </Box>
        </>
    );
};

export default StartQuiz;
