import React from 'react';
import {Box, Button, Center, Checkbox, Container, HStack, Input, Textarea, VStack} from "@chakra-ui/react";

const QuestionDialog = () => {
    return (
        <>
            <Box
                onClick={() => {
                    console.log("HAHHAHA")
                }}
                position={'fixed'}
                zIndex={9000}
                top={0}
                bg={'gray.300'}
                align={'center'}
                alignItems={'center'}
                opacity={.4}
                justifyContent={'center'}
                width={'100vw'}
                height={'100vh'}>
            </Box>

            <Box
                position={'fixed'}
                zIndex={9001}
                top={'50%'}
                right={'50%'}
                transform={
                    'translate(50%,-50%)'
                }
                minW={'500px'}
                bg={'white'}
                maxW={'500px'}
                // minH={'500px'}
                // top={10} right={50}
            >
                <VStack>
                    <Textarea placeholder={'Type your question here'}/>
                    <VStack p={4} spacing={4} w={'100%'}>
                        {
                            [1, 2, 3, 4].map((item, index) => (
                                <HStack w={'100%'}>
                                    <Checkbox/>
                                    <Input wordBreak={'break-all'} wordWrap={'break-word'} isFullWidth={true}
                                           size={'sm'}/>
                                </HStack>
                            ))
                        }
                    </VStack>
                    <Box> <Button size={'sm'} variant={'outline'} alignSelf={'start'} colorScheme={'teal'}>
                        Add answer option</Button></Box>
                    <Box w={'100%'}>
                        <HStack p={2} bg={'gray.700'} justifyContent={'end'}>
                            <Button size={'sm'}>Cancel</Button>
                            <Button size={'sm'}>Save</Button>
                        </HStack>
                    </Box>
                </VStack>
            </Box>
        </>

    );
};

export default QuestionDialog;
