import React, {useState} from 'react';
import {useAuth} from "../../../hooks/useAuth";
import {useHistory} from "react-router-dom";
import {
    Box, Button, Checkbox,
    Flex,
    FormControl, FormHelperText,
    FormLabel,
    Heading,
    Input,
    Link,
    Stack,
    Text,
    useColorModeValue
} from "@chakra-ui/react";

const SignupPage = () => {
    const {login} = useAuth();
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [remember, setRemember] = useState(true);
    const [error, setError] = useState(false);
    const history = useHistory();
    return (
        <Flex
            minH={"100vh"}
            align={"center"}
            justify={"center"}
            bg={useColorModeValue("gray.50", "gray.800")}
        >
            <Stack spacing={8} mx={"auto"} maxW={"lg"} minW={'lg'} py={12} px={6}>
                <Stack align={"center"}>
                    <Heading fontSize={"4xl"}>Register Account</Heading>
                    <Text fontSize={"lg"} color={"gray.600"}>
                        to enjoy all of our cool{" "}
                        <Link color={"blue.400"}>features</Link> ✌️
                    </Text>
                </Stack>
                <Box
                    rounded={"lg"}
                    bg={useColorModeValue("white", "gray.700")}
                    boxShadow={"lg"}
                    p={8}
                >
                    {/*Form*/}
                    <Stack spacing={4}>
                        {/*Username*/}
                        <FormControl id="username">
                            <FormLabel>Username</FormLabel>
                            <Input
                                value={username}
                                isInvalid={error}
                                errorBorderColor="crimson"
                                onChange={(e) => {
                                    setError(false);
                                    setUsername(e.target.value);
                                }}
                                type='email|text'/>
                            {
                                error &&
                                <FormHelperText color={'crimson'}>Username or password is not correct!</FormHelperText>
                            }
                        </FormControl>
                        {/*Email*/}
                        <FormControl id="email">
                            <FormLabel>Email address</FormLabel>
                            <Input
                                value={email}
                                isInvalid={error}
                                errorBorderColor="crimson"
                                onChange={(e) => {
                                    setError(false);
                                    setEmail(e.target.value);
                                }}
                                type='email|text'/>
                            {
                                error &&
                                <FormHelperText color={'crimson'}>Username or password is not correct!</FormHelperText>
                            }
                        </FormControl>
                        {/*Password*/}
                        <FormControl id="password">
                            <FormLabel>Password</FormLabel>
                            <Input
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                type="password"/>
                        </FormControl>
                        <Stack>
                            <Box minH={5}/>
                            <Button
                                disabled={
                                    (username.length === 0 || password.length === 0 || email.length === 0)
                                }
                                bg={"blue.400"}
                                color={"white"}
                                _hover={{
                                    bg: "blue.500",
                                }}
                                onClick={async (e) => {
                                    e.preventDefault();
                                    let s = await login(username, password, remember);
                                    if (s) {
                                        console.log("LoginPage success")
                                        history.replace('/');
                                    } else {
                                        setError(true);
                                        setPassword('');
                                    }
                                }}
                            >
                                Sign up
                            </Button>
                         
                        </Stack>
                    </Stack>
                </Box>
            </Stack>
        </Flex>
    );
};

export default SignupPage;
