import React, {useState, useContext, createContext, useEffect} from "react";
import Axios from "axios";
import UserModel from "../classess/UserModel";

// Provider hook that creates auth object and handle state
export const AuthProvider = ({children}) => {
    const [user, setUser] = useState({
        uid: null, username: null, email: null, avtImgUrl: null
    });
    // const [user, setUser] = useState(null);
    const [isAuthenticated, setAuthenticated] = useState(false);

    //login
    const login = async (username, password) => {
        try {
            let resp = await Axios.post("http://localhost:8080/api/v1/authenticate/login", {
                "email": username,
                "password": password
            });
            if (resp.status === 200) {
                let user = resp.data.user;
                localStorage.setItem("user", JSON.stringify(user));
                //save access token
                let accessToken = resp.data.access_token;
                localStorage.setItem("accessToken", accessToken);
                //save refresh token
                let refreshToken = resp.data.refresh_token;
                localStorage.setItem("refreshToken", refreshToken);
                console.log('sign in success');
                return true;
            }
            return false;
        } catch (e) {
            return false;
        }
    };

    //logout
    const logout = async () => {
        console.log('user logout');
        setUser({
            uid: null,
            username: null,
            email: null,
            avtImgUrl: null,
        });
        localStorage.removeItem('user')
        localStorage.removeItem('accessToken')
        localStorage.removeItem('refreshToken')
    }
    //check access token
    const getUserDetails = async () => {
        let data = JSON.parse(localStorage.getItem('user'));
        console.log(data)
        if (data == null) {
            setUser({
                uid: null,
                username: null,
                email: null,
                avtImgUrl: null,
            });
        } else {
            setUser(
                {
                    ...user,
                    uid: data.uid,
                    username: data.username,
                    email: data.email,
                    avtImgUrl: data.avtImgUrl,
                }
            )
        }

        return data;
    }
    //create value
    const value = {
        user,
        setUser,
        login,
        logout,
        isAuthenticated,
        setAuthenticated,
    };
    useEffect(async () => {
        await getUserDetails();
        // console.log(user);
    }, []);
    return (
        <AuthContext.Provider value={value}>{children}</AuthContext.Provider>
    );
};
//create auth context
const AuthContext = createContext();

export const useAuth = () => {
    return useContext(AuthContext);
};
