            (resp) => {
                //if success
                if (resp.status == 200) {
                    const data = resp.data;
                    let quiz = new QuizModel();
                    quiz.quizId = data.quizId;
                    quiz.title = data.title;
                    quiz.code = data.code;
                    let questions = [];
                    //get questions
                    data.questions.forEach((questionItem) => {
                        let question = new QuestionModel();
                        question.title = questionItem.title;
                        question.questionId = questionItem.questionId;
                        question.optionType = questionItem.optionType;
                        let options = [];
                        //get options
                        questionItem.options.forEach((optionItem) => {
                            let option = new OptionModel(
                                optionItem.optionId,
                                optionItem.content,
                                optionItem.score
                            );
                            options.push(option);
                        });
                        //add option to question
                        question.options = options;
                        //add to list question
                        questions.push(question);
                    });
                    //add questions to quiz
                    quiz.questions = questions;
                    //update state
                    setQuiz(quiz);
                    console.log(quiz);
                }
            }
        );
    };
