import React, {useEffect, useRef, useState} from 'react';
import {
    Box,
    Button,
    Checkbox, Container,
    Divider,
    Flex,
    Heading,
    HStack, IconButton,
    Progress,
    SimpleGrid,
    Text, Tooltip,
    VStack
} from "@chakra-ui/react";
import {useParams} from "react-router-dom";
import SettingDialog from "./SettingDialog";
import {ArrowDownIcon, ArrowUpIcon, TimeIcon} from "@chakra-ui/icons";
import {useQuiz} from "../../context/QuizProvider";
import axios from "axios";

const GradeQuiz = () => {

    //get id in params
    const {id} = useParams();
    const pageRef = useRef(null);
    const {foundedQuiz, setFoundedQuiz} = useQuiz();
    const [showSetting, setShowSetting] = useState(true);

    const [progress, setProgress] = useState(0);

    // const [userAnswers, setUserAnswers] = useState(new Map([
    //     [100, [1, 2, 3]],
    //     [2, [5, 23, 31]],
    // ]));
    const [userAnswers, setUserAnswers] = useState(new Map([]));

    useEffect(() => {
        setShowSetting(true);
        fetchQuiz();
        // console.log(userAnswers)
    }, []);
    const fetchQuiz = async () => {
        try {
            const resp = await axios.get(`http://localhost:8080/api/v1/public/quiz?code=${id}`);
            if (resp.status === 200) {
                setFoundedQuiz(resp.data);
            }
        } catch (e) {
        }
    }

    // [100, [1, 2, 3]],
    //     [2, [5, 23, 31]],

    function onSelectedOption(e, optionId, questionId) {
        if (userAnswers != null && userAnswers.size === 0) {
            console.log("Answer = 0")
            let tmp = ((1) / foundedQuiz.questions.length) * 100;
            setProgress(tmp);
            console.log(tmp);
        } else {
            if (userAnswers.has(questionId)) {
                console.log("Ole question")
            } else {
                console.log("New question addedd")
                let tmp = ((userAnswers.size + 1) / foundedQuiz.questions.length) * 100;
                setProgress(tmp);
            }

        }

        // console.log(e.target.checked, "QuestionID: ", questionId, " OptionID: ", optionId);
        let isChecked = e.target.checked;
        //create temp

        if (isChecked) {
            //create temp
            let tmp = userAnswers;
            //get optionIDs of question by questionID
            let a = tmp.get(questionId);
            if (a) {
                a.push(optionId);
            } else {
                a = [optionId];
            }
            tmp.set(questionId, a);
            //update userAnswers state
            setUserAnswers(new Map(tmp.entries()));
        } else {
            console.log('remove item')
            //create temp
            let tmp = userAnswers;
            //get optionIDs of question by questionID
            let a = tmp.get(questionId);
            if (a && a.includes(optionId)) {
                let newA = a.filter(item => item !== optionId);
                if (newA == null || newA.length === 0) {
                    console.log("DELETE OPTION !")
                    tmp.delete(questionId);
                    setUserAnswers(new Map(tmp.entries()));
                } else {
                    tmp.set(questionId, newA);
                    setUserAnswers(new Map(tmp.entries()));
                }

            }

        }


        //         setUserAnswers(prevState =>
        //             new Map([...prevState, [questionId,optionId]])
        //         );


    }

    return (
        <div ref={pageRef}>
            <Box
                minH={'100vh'}
                bg={'gray.100'}
                p={10}
                align={'center'}>

                {/*<Box minH={'80px'}></Box>*/}
                {showSetting && <SettingDialog setShow={setShowSetting}/>}
                {/*A4*/}
                {
                    !showSetting && (
                        <Box
                            m={10}
                            bg={'white'}
                            borderRadius={'md'}
                            boxShadow={'xl'}
                            minH={'100vh'}
                            maxW={'860px'}
                            p={10}>
                            <VStack position={'relative'}>
                                {/*
                        QUIZ TITLE
                        */}
                                <Heading
                                    pb={10}
                                    fontSize={25}>
                                    {foundedQuiz.title != null ? foundedQuiz.title : ''}
                                </Heading>
                                <Text
                                    fontWeight={'medium'}
                                    color={'teal'}
                                    letterSpacing={1}
                                    fontStyle={'italic'}
                                    position={'absolute'}
                                    top={0}
                                    right={0}
                                    m={5}>
                                    {foundedQuiz.questions.length} Questions
                                </Text>
                                {/*QUESTIONS*/}
                                {
                                    foundedQuiz.questions.map((question, i) => (
                                        <VStack
                                            w={'100%'}
                                            align={'start'}
                                            textAlign={'start'
                                            } key={i}
                                            p={2}
                                            spacing={5}>
                                            {/*
                                    QUESTION TITLE
                                    */}
                                            <Container w={'100%'} maxW="4xl">
                                                <Box maxW="4xl">
                                                    <Text fontWeight={'medium'}>
                                                        {i + 1}. {question.title} \t QuestionID : {question.questionId}
                                                    </Text>
                                                </Box>
                                            </Container>
                                            {/*OPTIONS*/}
                                            <SimpleGrid
                                                w={'100%'}
                                                columns={2}
                                                spacing={2}
                                                align={'start'}
                                                textAlign={'start'}>
                                                {
                                                    question.options.map((option, i) => (
                                                        <Container
                                                            w={'100%'}
                                                            minW={300}
                                                            key={i}
                                                            maxW="xl"
                                                            align={'start'}
                                                            textAlign={'start'}
                                                            alignItems={'start'}
                                                        >
                                                            <Box padding="1" maxW="3xl">
                                                                <Checkbox
                                                                    onChange={(e) => onSelectedOption(e, option.optionId, question.questionId)}
                                                                    value={option.optionId}
                                                                    colorScheme={'teal'}
                                                                    size={'md'}>
                                                                    {option.content} \t OptionID : {option.optionId}
                                                                </Checkbox>
                                                            </Box>
                                                        </Container>
                                                    ))
                                                }
                                            </SimpleGrid>
                                            <Divider/>
                                        </VStack>
                                    ))
                                }
                                {/* Submit button*/}
                                <Box py={5} align={'end'} alignSelf={'end'}>
                                    <Button alignSelf={'end'} size={'lg'} colorScheme={'teal'}>Submit</Button>
                                </Box>
                            </VStack>

                            {/* FIXED CONTENTS*/}
                            <HStack zIndex={100} position={'fixed'} bottom={10} right={20}>
                                <Tooltip label={"Scroll to top"}>
                                    <IconButton
                                        onClick={() => window.scrollTo(0, 0)}
                                        colorScheme={'teal'}
                                        icon={<ArrowUpIcon/>}/>
                                </Tooltip>
                                <Tooltip label={"Scroll to bottom"}>
                                    <IconButton
                                        // aria-label={""}
                                        onClick={() => {
                                            pageRef.current.scrollIntoView({
                                                behavior: "smooth",
                                                block: "end",
                                                inline: "nearest"
                                            })
                                        }}
                                        colorScheme={'teal'}
                                        icon={<ArrowDownIcon/>}/>
                                </Tooltip>
                            </HStack>
                            <Flex p={1} position={'fixed'} left={0} top={0} m={10}>
                                <Button size={'md'} colorScheme={'teal'} variant={'outline'} leftIcon={<TimeIcon/>}>23:00
                                    left</Button>
                            </Flex>
                            <Box
                                bg={'red'}
                                minW={'100vw'}
                                position={'fixed'}
                                bottom={0}
                                align={'start'}
                            >
                                <Progress
                                    // alignSelf={'start'}
                                    size={'sm'}
                                    // left={'50%'}
                                    // transform={'translateX(-50%)'}
                                    minW={'860px'}
                                    // maxW={'860px'}
                                    // minW={'100vw'}
                                    colorScheme={'red'}
                                    maxW={'860px'}
                                    position={'fixed'}
                                    bottom={0}
                                    value={progress}/>
                            </Box>

                        </Box>


                    )
                }


            </Box>

        </div>
    );
};

export default GradeQuiz;
